} from 'lucide-react';

// Enhanced animated counter component
const AnimatedCounter = ({ end, duration = 2000, prefix = '', suffix = '', decimals = 0 }) => {
  const [count, setCount] = useState(0);

  useEffect(() => {
    let startTime = null;
    const startValue = 0;

    const animate = (currentTime) => {
      if (startTime === null) startTime = currentTime;
      const progress = Math.min((currentTime - startTime) / duration, 1);

      const easeOutCubic = 1 - Math.pow(1 - progress, 3);
      const currentCount = startValue + (end - startValue) * easeOutCubic;

      setCount(currentCount);

      if (progress < 1) {
        requestAnimationFrame(animate);
      }
    };

    requestAnimationFrame(animate);
  }, [end, duration]);

  return (
    <span>
      {prefix}{count.toFixed(decimals)}{suffix}
    </span>
  );
};

// Floating particles background
const ParticleBackground = () => {
  const particles = Array.from({ length: 20 }, (_, i) => ({
    id: i,
    left: Math.random() * 100,
    animationDelay: Math.random() * 10,
    size: Math.random() * 4 + 2,
  }));

  return (
    <div className="fixed inset-0 pointer-events-none overflow-hidden">
      {particles.map(particle => (
        <div
          key={particle.id}
          className="absolute rounded-full bg-gradient-to-r from-blue-400 to-purple-500 opacity-10 animate-float"
          style={{
            left: `${particle.left}%`,
            width: `${particle.size}px`,
            height: `${particle.size}px`,
            animationDelay: `${particle.animationDelay}s`,
            animationDuration: `${8 + Math.random() * 4}s`,
          }}
        />
      ))}
    </div>
  );
};

// Enhanced Chart Component
const EnhancedChart = ({ data, type = 'line', height = 200, color = 'blue' }) => {
  const colorMaps = {
    blue: 'from-blue-500 to-cyan-500',
    green: 'from-green-500 to-emerald-500',
    red: 'from-red-500 to-orange-500',
    purple: 'from-purple-500 to-pink-500'
  };

  return (
    <div className="relative" style={{ height }}>
      <svg width="100%" height={height} viewBox={`0 0 600 ${height}`} className="overflow-visible">
        {/* Background grid */}
        <defs>
          <linearGradient id={`gradient-${color}`} x1="0%" y1="0%" x2="0%" y2="100%">
            <stop offset="0%" stopColor={color === 'blue' ? '#3b82f6' : color === 'green' ? '#10b981' : color === 'red' ? '#ef4444' : '#8b5cf6'} stopOpacity="0.3" />
            <stop offset="100%" stopColor={color === 'blue' ? '#3b82f6' : color === 'green' ? '#10b981' : color === 'red' ? '#ef4444' : '#8b5cf6'} stopOpacity="0.05" />
          </linearGradient>
        </defs>

        <rect width="600" height={height} fill="url(#grid)" opacity="0.1" />

        {/* Sample data visualization */}
        {type === 'line' && (
          <>
            <polyline
              fill="none"
              stroke={color === 'blue' ? '#3b82f6' : color === 'green' ? '#10b981' : color === 'red' ? '#ef4444' : '#8b5cf6'}
              strokeWidth="3"
              points={data.map((d, i) => `${(i / (data.length - 1)) * 560 + 20},${height - 40 - (d / 100) * (height - 80)}`).join(' ')}
            />
            <polygon
              fill={`url(#gradient-${color})`}
              points={`20,${height - 40} ${data.map((d, i) => `${(i / (data.length - 1)) * 560 + 20},${height - 40 - (d / 100) * (height - 80)}`).join(' ')} ${580},${height - 40}`}
            />
          </>
        )}

        {type === 'bar' && data.map((d, i) => (
          <rect
            key={i}
            x={i * (560 / data.length) + 30}
            y={height - 40 - (d / 100) * (height - 80)}
            width={Math.max(40, 560 / data.length - 10)}
            height={(d / 100) * (height - 80)}
            fill={`url(#gradient-${color})`}
            className="hover:opacity-80 transition-opacity cursor-pointer"
          />
        ))}
      </svg>
    </div>
  );
};

// Mock data with enhanced properties
const generatePortfolioData = () => {
  const farms = [];
  const regions = ['Yorkshire', 'Somerset', 'Norfolk', 'Devon', 'Lincolnshire', 'Kent'];
  const farmTypes = ['Dairy', 'Arable', 'Mixed', 'Livestock'];

  for (let i = 0; i < 150; i++) {
    const loanAmount = Math.random() * 2000000 + 100000;
    const emissions = Math.random() * 2 + 0.5;
    const riskScore = Math.random() * 100;

    farms.push({
      id: `FARM_${i.toString().padStart(3, '0')}`,
      name: `${regions[Math.floor(Math.random() * regions.length)]} Farm ${i + 1}`,
      region: regions[Math.floor(Math.random() * regions.length)],
      type: farmTypes[Math.floor(Math.random() * farmTypes.length)],
      loanAmount,
      outstandingBalance: loanAmount * (0.3 + Math.random() * 0.7),
      interestRate: 3.5 + Math.random() * 3,
      creditRating: ['A', 'B', 'C', 'D'][Math.floor(Math.random() * 4)],
      riskScore,
      riskCategory: riskScore > 70 ? 'High' : riskScore > 40 ? 'Medium' : 'Low',
      emissions,
      emissionsCategory: emissions > 1.5 ? 'High' : emissions > 1.0 ? 'Medium' : 'Low',
      sustainabilityScore: Math.random() * 100,
      transitionStatus: ['Not Started', 'Planning', 'In Progress', 'Advanced'][Math.floor(Math.random() * 4)],
      lastUpdate: new Date(Date.now() - Math.random() * 90 * 24 * 60 * 60 * 1000),
      coordinates: {
        lat: 51 + Math.random() * 5,
        lng: -4 + Math.random() * 6
      },
      metrics: {
        milkYield: 6000 + Math.random() * 4000,
        herdSize: 50 + Math.random() * 300,
        landSize: 50 + Math.random() * 500,
        netZeroTarget: 2030 + Math.floor(Math.random() * 20),
        sequestrationPotential: Math.random() * 100
      },
      trendDirection: Math.random() > 0.5 ? 'up' : 'down',
      performanceScore: Math.random() * 100,
      // Additional risk factors
      climatRiskFactors: {
        drought: Math.random() * 100,
        flooding: Math.random() * 100,
        temperature: Math.random() * 100,
        precipitation: Math.random() * 100
      },
      financialRiskFactors: {
        debtToEquity: Math.random() * 3,
        cashFlow: Math.random() * 100,
        profitMargin: Math.random() * 30,
        marketVolatility: Math.random() * 100
      },
      // Emissions breakdown
      emissionsBreakdown: {
        livestock: Math.random() * 1000,
        fertilizer: Math.random() * 500,
        machinery: Math.random() * 300,
        energy: Math.random() * 200
      },
      // Transition opportunities
      transitionOpportunities: {
        solarPotential: Math.random() * 100,
        windPotential: Math.random() * 100,
        biogas: Math.random() * 100,
        precisionAg: Math.random() * 100,
        carbonSequestration: Math.random() * 100
      }
    });
  }

  return farms;
};

const bankingInsightsData = {
  totalPortfolio: 2.4e9,
  totalFarms: 150,
  averageEmissions: 1.42,
  portfolioRisk: 'Medium',
  transitionFinanceOpportunity: 340e6,
  financedEmissions: 2.8e6,
  riskDistribution: {
    low: 45,
    medium: 78,
    high: 27
  },
  regionalExposure: {
    'Yorkshire': 380e6,
    'Somerset': 290e6,
    'Norfolk': 420e6,
    'Devon': 260e6,
    'Lincolnshire': 340e6,
    'Kent': 310e6
  }
};

export default function BankingInsightsDashboard() {
  const [activeTab, setActiveTab] = useState('portfolio');
  const [selectedRegion, setSelectedRegion] = useState('All');
  const [selectedRisk, setSelectedRisk] = useState('All');
  const [selectedTransition, setSelectedTransition] = useState('All');
  const [scenarioMode, setScenarioMode] = useState(false);
  const [farms] = useState(generatePortfolioData());
  const [refreshing, setRefreshing] = useState(false);
  const [hoverCard, setHoverCard] = useState(null);

  // Scenario parameters
  const [fertilizerCostIncrease, setFertilizerCostIncrease] = useState(0);
  const [interestRateChange, setInterestRateChange] = useState(0);
  const [droughtRisk, setDroughtRisk] = useState(false);
  const [carbonPrice, setCarbonPrice] = useState(25);

  // Filter farms based on selections
  const filteredFarms = farms.filter(farm => {
    return (selectedRegion === 'All' || farm.region === selectedRegion) &&
           (selectedRisk === 'All' || farm.riskCategory === selectedRisk) &&
           (selectedTransition === 'All' || farm.transitionStatus === selectedTransition);
  });

  // Calculate scenario impacts
  const calculateScenarioImpact = (farm) => {
    let riskAdjustment = 0;

    if (fertilizerCostIncrease > 0) {
      riskAdjustment += (fertilizerCostIncrease / 100) * 15;
    }

    if (interestRateChange !== 0) {
      riskAdjustment += Math.abs(interestRateChange) * 8;
    }

    if (droughtRisk) {
      riskAdjustment += farm.type === 'Arable' ? 25 : 15;
    }

    return Math.min(100, farm.riskScore + riskAdjustment);
  };

  // Export functionality
  const exportPortfolioData = () => {
    const data = filteredFarms.map(farm => ({
      'Farm ID': farm.id,
      'Farm Name': farm.name,
      'Region': farm.region,
      'Type': farm.type,
      'Loan Amount (£)': farm.loanAmount.toFixed(2),
      'Outstanding Balance (£)': farm.outstandingBalance.toFixed(2),
      'Interest Rate (%)': farm.interestRate.toFixed(2),
      'Credit Rating': farm.creditRating,
      'Risk Score': scenarioMode ? calculateScenarioImpact(farm).toFixed(1) : farm.riskScore.toFixed(1),
      'Risk Category': farm.riskCategory,
      'Emissions (kg CO2e/L)': farm.emissions.toFixed(3),
      'Sustainability Score': farm.sustainabilityScore.toFixed(1),
      'Transition Status': farm.transitionStatus,
      'Net Zero Target': farm.metrics.netZeroTarget,
      'Sequestration Potential': farm.metrics.sequestrationPotential.toFixed(1)
    }));

    const csv = [
      Object.keys(data[0]).join(','),
      ...data.map(row => Object.values(row).join(','))
    ].join('\n');

    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `agricultural_portfolio_${new Date().toISOString().split('T')[0]}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
  };

  // Risk heat map data
  const riskHeatMap = filteredFarms.reduce((acc, farm) => {
    const risk = scenarioMode ? 
      (calculateScenarioImpact(farm) > 70 ? 'High' : calculateScenarioImpact(farm) > 40 ? 'Medium' : 'Low') :
      farm.riskCategory;

    if (!acc[farm.region]) {
      acc[farm.region] = { High: 0, Medium: 0, Low: 0, total: 0 };
    }
    acc[farm.region][risk]++;
    acc[farm.region].total++;
    return acc;
  }, {});

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 relative overflow-hidden">
      {/* Add custom styles */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          33% { transform: translateY(-10px) rotate(120deg); }
          66% { transform: translateY(10px) rotate(240deg); }
        }

        @keyframes pulse-glow {
          0%, 100% { box-shadow: 0 0 20px rgba(59, 130, 246, 0.3); }
          50% { box-shadow: 0 0 30px rgba(59, 130, 246, 0.6); }
        }

        @keyframes shimmer {
          0% { background-position: -200% 0; }
          100% { background-position: 200% 0; }
        }

        .animate-float {
          animation: float linear infinite;
        }

        .animate-pulse-glow {
          animation: pulse-glow 2s ease-in-out infinite;
        }

        .animate-shimmer {
          animation: shimmer 2s linear infinite;
          background: linear-gradient(90deg, transparent, rgba(255,255,255,0.1), transparent);
          background-size: 200% 100%;
        }

        .glass-morphism {
          background: rgba(255, 255, 255, 0.1);
          backdrop-filter: blur(10px);
          border: 1px solid rgba(255, 255, 255, 0.2);
        }

        .glass-morphism-dark {
          background: rgba(0, 0, 0, 0.2);
          backdrop-filter: blur(15px);
          border: 1px solid rgba(255, 255, 255, 0.1);
        }

        .gradient-text {
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }

        .holographic {
          background: linear-gradient(135deg, 
            #667eea 0%, 
            #764ba2 25%, 
            #f093fb 50%, 
            #f5576c 75%, 
            #4facfe 100%);
          background-size: 300% 300%;
          animation: holographic-shift 6s ease infinite;
        }

        @keyframes holographic-shift {
          0%, 100% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
        }
      `}</style>

      <ParticleBackground />

      {/* Header */}
      <div className="glass-morphism-dark border-b border-white/10 relative z-10">
        <div className="max-w-7xl mx-auto px-6 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-6">
              <div className="flex items-center">
                <div className="relative">
                  <Building2 className="text-blue-400 mr-4 animate-pulse-glow" size={32} />
                  <div className="absolute -top-1 -right-1 w-3 h-3 bg-green-400 rounded-full animate-ping"></div>
                </div>
                <div>
                  <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-400 via-purple-400 to-emerald-400 bg-clip-text text-transparent">
                    Agricultural Portfolio Insights
                  </h1>
                  <p className="text-gray-300 mt-1">Next-Generation Banking Dashboard for Sustainable Agriculture</p>
                </div>
              </div>
            </div>

            <div className="flex items-center space-x-6">
              <div className="text-right group">
                <div className="text-3xl font-bold bg-gradient-to-r from-emerald-400 to-blue-400 bg-clip-text text-transparent">
                  £<AnimatedCounter end={bankingInsightsData.totalPortfolio / 1e9} decimals={1} suffix="B" />
                </div>
                <div className="text-xs text-gray-400 group-hover:text-emerald-400 transition-colors">Total Portfolio</div>
              </div>
              <div className="text-right group">
                <div className="text-3xl font-bold bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent">
                  <AnimatedCounter end={bankingInsightsData.totalFarms} />
                </div>
                <div className="text-xs text-gray-400 group-hover:text-purple-400 transition-colors">Active Farms</div>
              </div>
              <button
                onClick={() => {
                  setRefreshing(true);
                  setTimeout(() => setRefreshing(false), 1500);
                }}
                className="group relative overflow-hidden bg-gradient-to-r from-blue-600 to-purple-600 text-white px-6 py-3 rounded-xl hover:from-blue-500 hover:to-purple-500 transition-all duration-300 transform hover:scale-105 hover:shadow-2xl"
              >
                <div className="absolute inset-0 bg-gradient-to-r from-white/0 via-white/20 to-white/0 translate-x-[-100%] group-hover:translate-x-[100%] transition-transform duration-700"></div>
                <div className="relative flex items-center space-x-2">
                  <RefreshCw className={`${refreshing ? 'animate-spin' : ''} transition-transform`} size={18} />
                  <span className="font-medium">Refresh Data</span>
                </div>
              </button>
            </div>
          </div>

          {/* Enhanced Tab Navigation */}
          <div className="flex space-x-2 mt-8 bg-black/20 p-2 rounded-2xl backdrop-blur">
            {[
              { id: 'portfolio', label: 'Portfolio Overview', icon: BarChart3, color: 'from-blue-500 to-cyan-500' },
              { id: 'risk', label: 'Risk Assessment', icon: AlertTriangle, color: 'from-red-500 to-orange-500' },
              { id: 'emissions', label: 'Financial Emissions', icon: Leaf, color: 'from-green-500 to-emerald-500' },
              { id: 'transition', label: 'Transition Finance', icon: Target, color: 'from-purple-500 to-pink-500' },
              { id: 'supply', label: 'Supply Chain', icon: Globe, color: 'from-indigo-500 to-purple-500' }
            ].map(tab => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`group relative flex items-center space-x-3 px-6 py-4 rounded-xl transition-all duration-300 ${
                  activeTab === tab.id
                    ? `bg-gradient-to-r ${tab.color} text-white shadow-2xl transform scale-105`
                    : 'text-gray-300 hover:text-white hover:bg-white/10'
                }`}
              >
                <tab.icon size={20} className="relative z-10" />
                <span className="font-medium relative z-10">{tab.label}</span>
                {activeTab === tab.id && (
                  <div className="absolute inset-0 bg-gradient-to-r from-white/0 via-white/20 to-white/0 rounded-xl animate-shimmer"></div>
                )}
              </button>
            ))}
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-6 py-8 relative z-10">
        {/* Enhanced Filters */}
        <div className="glass-morphism rounded-2xl p-6 mb-8 border border-white/10 shadow-2xl">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-6">
              <div className="flex items-center space-x-3">
                <Filter size={20} className="text-blue-400" />
                <span className="text-lg font-semibold text-white">Smart Filters</span>
              </div>

              <div className="flex space-x-4">
                <select
                  value={selectedRegion}
                  onChange={(e) => setSelectedRegion(e.target.value)}
                  className="px-4 py-2 bg-black/30 text-white border border-white/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 backdrop-blur"
                >
                  <option value="All">All Regions</option>
                  {Object.keys(bankingInsightsData.regionalExposure).map(region => (
                    <option key={region} value={region}>{region}</option>
                  ))}
                </select>

                <select
                  value={selectedRisk}
                  onChange={(e) => setSelectedRisk(e.target.value)}
                  className="px-4 py-2 bg-black/30 text-white border border-white/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500 backdrop-blur"
                >
                  <option value="All">All Risk Levels</option>
                  <option value="Low">Low Risk</option>
                  <option value="Medium">Medium Risk</option>
                  <option value="High">High Risk</option>
                </select>

                <select
                  value={selectedTransition}
                  onChange={(e) => setSelectedTransition(e.target.value)}
                  className="px-4 py-2 bg-black/30 text-white border border-white/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 backdrop-blur"
                >
                  <option value="All">All Transition Status</option>
                  <option value="Not Started">Not Started</option>
                  <option value="Planning">Planning</option>
                  <option value="In Progress">In Progress</option>
                  <option value="Advanced">Advanced</option>
                </select>
              </div>
            </div>

            <div className="flex items-center space-x-6">
              <label className="flex items-center space-x-3 group">
                <input
                  type="checkbox"
                  checked={scenarioMode}
                  onChange={(e) => setScenarioMode(e.target.checked)}
                  className="w-5 h-5 rounded border-2 border-white/30 bg-black/30 text-blue-500 focus:ring-blue-500 focus:ring-2"
                />
                <span className="text-white font-medium group-hover:text-blue-400 transition-colors">Scenario Mode</span>
                <Zap className="text-yellow-400 animate-pulse" size={16} />
              </label>

              <button
                onClick={exportPortfolioData}
                className="group relative overflow-hidden bg-gradient-to-r from-emerald-600 to-teal-600 text-white px-6 py-3 rounded-xl hover:from-emerald-500 hover:to-teal-500 transition-all duration-300 transform hover:scale-105 hover:shadow-2xl"
              >
                <div className="absolute inset-0 bg-gradient-to-r from-white/0 via-white/20 to-white/0 translate-x-[-100%] group-hover:translate-x-[100%] transition-transform duration-700"></div>
                <div className="relative flex items-center space-x-2">
                  <Download size={18} />
                  <span className="font-medium">Export Data</span>
                </div>
              </button>
            </div>
          </div>

          {/* Enhanced Scenario Controls */}
          {scenarioMode && (
            <div className="mt-6 pt-6 border-t border-white/20">
              <div className="bg-gradient-to-r from-yellow-500/20 to-orange-500/20 rounded-xl p-6 border border-yellow-500/30">
                <h3 className="text-lg font-bold text-yellow-300 mb-4 flex items-center">
                  <Zap className="mr-2 animate-pulse" size={20} />
                  Advanced Scenario Parameters
                </h3>
                <div className="grid grid-cols-2 lg:grid-cols-4 gap-6">
                  <div className="space-y-2">
                    <label className="block text-sm text-yellow-200 font-medium">Fertilizer Cost Increase (%)</label>
                    <input
                      type="range"
                      min="0"
                      max="100"
                      value={fertilizerCostIncrease}
                      onChange={(e) => setFertilizerCostIncrease(parseInt(e.target.value))}
                      className="w-full h-2 bg-black/30 rounded-lg appearance-none cursor-pointer slider"
                    />
                    <div className="text-sm text-yellow-300 font-bold">{fertilizerCostIncrease}%</div>
                  </div>

                  <div className="space-y-2">
                    <label className="block text-sm text-yellow-200 font-medium">Interest Rate Change (%)</label>
                    <input
                      type="range"
                      min="-2"
                      max="5"
                      step="0.1"
                      value={interestRateChange}
                      onChange={(e) => setInterestRateChange(parseFloat(e.target.value))}
                      className="w-full h-2 bg-black/30 rounded-lg appearance-none cursor-pointer"
                    />
                    <div className="text-sm text-yellow-300 font-bold">
                      {interestRateChange > 0 ? '+' : ''}{interestRateChange}%
                    </div>
                  </div>

                  <div className="space-y-2">
                    <label className="block text-sm text-yellow-200 font-medium">Carbon Price (£/tonne)</label>
                    <input
                      type="range"
                      min="10"
                      max="100"
                      value={carbonPrice}
                      onChange={(e) => setCarbonPrice(parseInt(e.target.value))}
                      className="w-full h-2 bg-black/30 rounded-lg appearance-none cursor-pointer"
                    />
                    <div className="text-sm text-yellow-300 font-bold">£{carbonPrice}</div>
                  </div>

                  <div className="flex items-center justify-center">
                    <label className="flex items-center space-x-3 group">
                      <input
                        type="checkbox"
                        checked={droughtRisk}
                        onChange={(e) => setDroughtRisk(e.target.checked)}
                        className="w-5 h-5 rounded border-2 border-red-500/50 bg-black/30 text-red-500 focus:ring-red-500"
                      />
                      <span className="text-sm font-bold text-red-300 group-hover:text-red-200 transition-colors">
                        Drought Risk Scenario
                      </span>
                    </label>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Portfolio Overview Tab */}
        {activeTab === 'portfolio' && (
          <div className="space-y-8">
            {/* Enhanced Key Metrics Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              <div 
