                      {(() => {
                        const years = [2024, 2025, 2026, 2027, 2028, 2029, 2030];
                        const currentEmissions = filteredFarms.reduce((sum, farm) => 
                          sum + (farm.outstandingBalance * farm.emissions / 1000), 0
                        ) / 1000;

                        const projectedEmissions = years.map((year, index) => ({
                          year,
                          business: currentEmissions * (1 - index * 0.02), // Business as usual: 2% annual reduction
                          target: currentEmissions * (1 - index * 0.08), // Target: 8% annual reduction
                          actual: index === 0 ? currentEmissions : currentEmissions * (1 - index * 0.05) // Actual: 5% annual reduction
                        }));

                        const maxEmissions = Math.max(...projectedEmissions.map(p => p.business));
                        const minEmissions = Math.min(...projectedEmissions.map(p => p.target));

                        const scaleY = (value) => 200 - ((value - minEmissions) / (maxEmissions - minEmissions)) * 160;
                        const scaleX = (index) => 60 + (index / (years.length - 1)) * 480;

                        return (
                          <>
                            {/* Grid */}
                            <rect x="60" y="40" width="480" height="160" fill="rgba(255,255,255,0.02)" rx="4" />

                            {/* Grid lines */}
                            {[0, 25, 50, 75, 100].map(pct => {
                              const y = 200 - (pct / 100 * 160);
                              return (
                                <line key={pct} x1="60" y1={y} x2="540" y2={y} stroke="rgba(255,255,255,0.1)" strokeWidth="1" strokeDasharray="3,3" />
