                              stroke="#ef4444"
                              strokeWidth="2"
                              strokeDasharray="5,5"
                              points={projectedEmissions.map((p, i) => `${scaleX(i)},${scaleY(p.business)}`).join(' ')}
                            />

                            {/* Target line */}
                            <polyline
                              fill="none"
                              stroke="#10b981"
                              strokeWidth="3"
                              points={projectedEmissions.map((p, i) => `${scaleX(i)},${scaleY(p.target)}`).join(' ')}
                            />

                            {/* Actual line */}
                            <polyline
                              fill="none"
                              stroke="#3b82f6"
                              strokeWidth="3"
                              points={projectedEmissions.slice(0, 2).map((p, i) => `${scaleX(i)},${scaleY(p.actual)}`).join(' ')}
                            />

                            {/* Fill area under target line */}
                            <polygon
                              fill="url(#emissions-gradient)"
                              points={`60,200 ${projectedEmissions.map((p, i) => `${scaleX(i)},${scaleY(p.target)}`).join(' ')} 540,200`}
                              opacity="0.2"
                            />

                            {/* Gradient definition */}
                            <defs>
                              <linearGradient id="emissions-gradient" x1="0%" y1="0%" x2="0%" y2="100%">
                                <stop offset="0%" stopColor="#10b981" stopOpacity="0.3" />
                                <stop offset="100%" stopColor="#10b981" stopOpacity="0.05" />
                              </linearGradient>
                            </defs>

                            {/* Data points */}
                            {projectedEmissions.map((p, i) => (
                              <g key={i}>
                                <circle cx={scaleX(i)} cy={scaleY(p.target)} r="4" fill="#10b981" />
                                {i < 2 && <circle cx={scaleX(i)} cy={scaleY(p.actual)} r="4" fill="#3b82f6" />}
                              </g>
                            ))}

                            {/* X-axis labels */}
                            {years.map((year, i) => (
                              <text key={i} x={scaleX(i)} y="220" textAnchor="middle" fontSize="11" fill="#9ca3af">
                                {year}
                              </text>
                            ))}

