              <div className="glass-morphism rounded-2xl p-6 border border-white/10 shadow-2xl relative overflow-hidden">
                <div className="absolute top-0 right-0 w-16 h-16 bg-gradient-to-br from-blue-500/20 to-cyan-500/20 rounded-full -translate-y-8 translate-x-8"></div>
                <div className="relative">
                  <div className="flex items-center justify-between mb-4">
                    <Truck className="text-blue-400" size={28} />
                    <Users className="text-blue-400" size={20} />
                  </div>
                  <p className="text-gray-300 text-sm mb-2">Supply Chain Farms</p>
                  <p className="text-3xl font-bold bg-gradient-to-r from-blue-400 to-cyan-400 bg-clip-text text-transparent">
                    <AnimatedCounter end={filteredFarms.length} />
                  </p>
                  <p className="text-xs text-blue-400 flex items-center mt-2">
                    <Truck size={12} className="mr-1" />
                    Active suppliers in portfolio
                  </p>
                </div>
              </div>

              <div className="glass-morphism rounded-2xl p-6 border border-white/10 shadow-2xl relative overflow-hidden">
                <div className="absolute top-0 right-0 w-16 h-16 bg-gradient-to-br from-red-500/20 to-orange-500/20 rounded-full -translate-y-8 translate-x-8"></div>
                <div className="relative">
                  <div className="flex items-center justify-between mb-4">
                    <AlertTriangle className="text-red-400" size={28} />
                    <TrendingUp className="text-red-400" size={20} />
                  </div>
                  <p className="text-gray-300 text-sm mb-2">High Risk Suppliers</p>
                  <p className="text-3xl font-bold bg-gradient-to-r from-red-400 to-orange-400 bg-clip-text text-transparent">
                    <AnimatedCounter 
                      end={filteredFarms.filter(f => (scenarioMode ? calculateScenarioImpact(f) : f.riskScore) > 70).length}
                    />
                  </p>
                  <p className="text-xs text-red-400 flex items-center mt-2">
                    <AlertTriangle size={12} className="mr-1" />
                    Suppliers needing attention
                  </p>
                </div>
              </div>

              <div className="glass-morphism rounded-2xl p-6 border border-white/10 shadow-2xl relative overflow-hidden">
                <div className="absolute top-0 right-0 w-16 h-16 bg-gradient-to-br from-orange-500/20 to-yellow-500/20 rounded-full -translate-y-8 translate-x-8"></div>
                <div className="relative">
                  <div className="flex items-center justify-between mb-4">
                    <MapPin className="text-orange-400" size={28} />
                    <Globe className="text-orange-400" size={20} />
                  </div>
                  <p className="text-gray-300 text-sm mb-2">Geographic Concentration</p>
                  <p className="text-3xl font-bold bg-gradient-to-r from-orange-400 to-yellow-400 bg-clip-text text-transparent">
                    <AnimatedCounter 
                      end={Math.max(...Object.values(riskHeatMap).map(r => r.total))}
                    />
                  </p>
                  <p className="text-xs text-orange-400 flex items-center mt-2">
                    <MapPin size={12} className="mr-1" />
                    Max farms per region
                  </p>
                </div>
              </div>

              <div className="glass-morphism rounded-2xl p-6 border border-white/10 shadow-2xl relative overflow-hidden">
                <div className="absolute top-0 right-0 w-16 h-16 bg-gradient-to-br from-green-500/20 to-emerald-500/20 rounded-full -translate-y-8 translate-x-8"></div>
                <div className="relative">
                  <div className="flex items-center justify-between mb-4">
                    <Shield className="text-green-400" size={28} />
                    <CheckCircle className="text-green-400" size={20} />
                  </div>
                  <p className="text-gray-300 text-sm mb-2">Supply Resilience</p>
                  <p className="text-3xl font-bold bg-gradient-to-r from-green-400 to-emerald-400 bg-clip-text text-transparent">
                    <AnimatedCounter 
                      end={((filteredFarms.filter(f => f.riskCategory === 'Low').length / filteredFarms.length) * 100)}
                      decimals={0}
                      suffix="%"
                    />
                  </p>
                  <p className="text-xs text-green-400 flex items-center mt-2">
                    <Shield size={12} className="mr-1" />
                    Low risk suppliers
                  </p>
                </div>
              </div>
            </div>

            {/* Supply Chain Interactive Map */}
            <div className="glass-morphism rounded-2xl p-8 border border-white/10 shadow-2xl">
              <h3 className="text-xl font-bold text-white mb-6 flex items-center">
                <Globe className="mr-3 text-blue-400" size={24} />
                Supply Chain Risk Mapping
              </h3>

              <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                <div className="lg:col-span-2">
                  <div className="relative h-96 bg-gradient-to-br from-blue-900/20 to-purple-900/20 rounded-xl border border-white/10 flex items-center justify-center overflow-hidden">
                    {/* Simulated map visualization */}
                    <div className="absolute inset-0 opacity-20">
                      {Array.from({ length: 50 }, (_, i) => (
                        <div
                          key={i}
                          className={`absolute w-2 h-2 rounded-full animate-pulse ${
                            i % 3 === 0 ? 'bg-red-400' : i % 3 === 1 ? 'bg-yellow-400' : 'bg-green-400'
                          }`}
                          style={{
                            left: `${10 + Math.random() * 80}%`,
                            top: `${10 + Math.random() * 80}%`,
                            animationDelay: `${Math.random() * 2}s`
                          }}
                        />
                      ))}
                    </div>

                    <div className="text-center z-10">
                      <MapPin className="mx-auto text-blue-400 mb-4 animate-bounce" size={48} />
                      <p className="text-white text-lg font-semibold mb-2">Interactive Supply Chain Map</p>
                      <p className="text-gray-300 text-sm max-w-md">
                        Real-time visualization of farm locations with risk-based color coding and supply chain connections
                      </p>
                    </div>
                  </div>

                  <div className="mt-4 flex justify-center space-x-6">
                    <div className="flex items-center space-x-2">
                      <div className="w-3 h-3 bg-green-400 rounded-full animate-pulse"></div>
                      <span className="text-xs text-gray-400">Low Risk ({filteredFarms.filter(f => f.riskCategory === 'Low').length})</span>
                    </div>
                    <div className="flex items-center space-x-2">
                      <div className="w-3 h-3 bg-yellow-400 rounded-full animate-pulse"></div>
                      <span className="text-xs text-gray-400">Medium Risk ({filteredFarms.filter(f => f.riskCategory === 'Medium').length})</span>
                    </div>
                    <div className="flex items-center space-x-2">
                      <div className="w-3 h-3 bg-red-400 rounded-full animate-pulse"></div>
                      <span className="text-xs text-gray-400">High Risk ({filteredFarms.filter(f => f.riskCategory === 'High').length})</span>
                    </div>
                  </div>
                </div>

                <div className="space-y-4">
                  <h4 className="text-lg font-semibold text-white">Key Metrics</h4>

                  <div className="bg-black/20 rounded-lg p-4 border border-white/10">
                    <div className="flex justify-between items-center mb-2">
                      <span className="text-gray-300">Supply Diversity Index</span>
                      <span className="font-bold text-blue-400">7.2/10</span>
                    </div>
                    <div className="w-full bg-black/30 rounded-full h-2">
                      <div className="h-full bg-gradient-to-r from-blue-500 to-cyan-500 rounded-full" style={{ width: '72%' }}></div>
                    </div>
                  </div>

                  <div className="bg-black/20 rounded-lg p-4 border border-white/10">
                    <div className="flex justify-between items-center mb-2">
                      <span className="text-gray-300">Geographic Spread</span>
                      <span className="font-bold text-green-400">{Object.keys(bankingInsightsData.regionalExposure).length} regions</span>
                    </div>
                    <div className="text-xs text-gray-400">Across UK agricultural zones</div>
                  </div>

                  <div className="bg-black/20 rounded-lg p-4 border border-white/10">
                    <div className="flex justify-between items-center mb-2">
                      <span className="text-gray-300">Critical Dependencies</span>
                      <span className="font-bold text-orange-400">3 suppliers</span>
                    </div>
                    <div className="text-xs text-gray-400">&gt;10% of total volume each</div>
                  </div>

                  <div className="bg-black/20 rounded-lg p-4 border border-white/10">
                    <div className="flex justify-between items-center mb-2">
                      <span className="text-gray-300">Backup Coverage</span>
                      <span className="font-bold text-purple-400">85%</span>
                    </div>
                    <div className="text-xs text-gray-400">Suppliers with alternatives</div>
                  </div>
                </div>
              </div>
            </div>

            {/* Critical Suppliers Analysis */}
            <div className="glass-morphism rounded-2xl p-8 border border-orange-500/30 shadow-2xl bg-gradient-to-r from-orange-500/5 to-red-500/5">
              <h3 className="text-xl font-bold text-white mb-6 flex items-center">
                <AlertTriangle className="mr-3 text-orange-400 animate-pulse" size={24} />
                Critical Suppliers Requiring Action
              </h3>

              <div className="overflow-x-auto">
                <table className="w-full text-sm">
                  <thead className="bg-orange-500/20 border border-orange-500/30">
                    <tr>
                      <th className="text-left p-4 font-semibold text-orange-300">Supplier</th>
                      <th className="text-left p-4 font-semibold text-orange-300">Risk Factors</th>
                      <th className="text-right p-4 font-semibold text-orange-300">Criticality</th>
                      <th className="text-right p-4 font-semibold text-orange-300">Supply Volume</th>
                      <th className="text-left p-4 font-semibold text-orange-300">Mitigation Plan</th>
                      <th className="text-left p-4 font-semibold text-orange-300">Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredFarms
                      .filter(f => (scenarioMode ? calculateScenarioImpact(f) : f.riskScore) > 60)
                      .sort((a, b) => b.outstandingBalance - a.outstandingBalance)
                      .slice(0, 6)
                      .map(farm => {
                        const riskScore = scenarioMode ? calculateScenarioImpact(farm) : farm.riskScore;
                        const estimatedVolume = Math.round(farm.metrics.milkYield * farm.metrics.herdSize / 1000);

                        return (
                          <tr key={farm.id} className="border-b border-orange-500/20 hover:bg-orange-500/10 transition-all duration-300">
                            <td className="p-4">
                              <div className="font-semibold text-white">{farm.name}</div>
                              <div className="text-xs text-gray-400">{farm.region} • {farm.type}</div>
                            </td>
                            <td className="p-4">
                              <div className="space-y-1">
                                {riskScore > 80 && (
                                  <span className="inline-block bg-red-500/20 text-red-300 px-2 py-1 rounded text-xs mr-1">
                                    Very High Risk
                                  </span>
                                )}
                                {farm.region === 'Norfolk' && droughtRisk && (
                                  <span className="inline-block bg-orange-500/20 text-orange-300 px-2 py-1 rounded text-xs">
                                    Drought Zone
                                  </span>
                                )}
                                {farm.emissions > 1.5 && (
                                  <span className="inline-block bg-yellow-500/20 text-yellow-300 px-2 py-1 rounded text-xs">
                                    High Emissions
                                  </span>
                                )}
                              </div>
                            </td>
                            <td className="p-4 text-right">
                              <span className={`inline-block px-3 py-1 rounded-full text-xs font-bold ${
                                farm.outstandingBalance > 500000 ? 'bg-red-500/20 text-red-300 border border-red-500/30' : 
                                'bg-yellow-500/20 text-yellow-300 border border-yellow-500/30'
                              }`}>
                                {farm.outstandingBalance > 500000 ? 'Critical' : 'Important'}
                              </span>
                            </td>
                            <td className="p-4 text-right font-bold text-white">{estimatedVolume}k L/year</td>
                            <td className="p-4 text-xs">
                              <div className="text-gray-300">• Enhanced monitoring</div>
                              <div className="text-gray-500">• Backup supplier identification</div>
                              <div className="text-gray-500">• Risk diversification</div>
                            </td>
                            <td className="p-4">
                              <span className="inline-block bg-blue-500/20 text-blue-300 px-3 py-1 rounded-full text-xs font-medium border border-blue-500/30">
                                In Progress
                              </span>
                            </td>
                          </tr>
                        );
                      })}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
