function todayISO(d = new Date()) {
  const y = d.getFullYear();
  const m = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  return `${y}-${m}-${day}`;
}

function loadState() {
  try {
    const raw = localStorage.getItem(STORAGE_KEY);
    if (!raw) return { 
      entries: [], 
      darkMode: true, 
      gameFaces: [], 
      dailyPositives: {}, 
      risksTaken: [],
      spfRules: SPF_RULES.reduce((acc, rule) => ({...acc, [rule.id]: rule.default}), {}),
      spfExposures: {},
      spfMetrics: {},
      worryWindow: []
    };
    const parsed = JSON.parse(raw);
    return { 
      entries: parsed.entries || [], 
      darkMode: parsed.darkMode ?? true,
      gameFaces: parsed.gameFaces || [],
      dailyPositives: parsed.dailyPositives || {},
      risksTaken: parsed.risksTaken || [],
      lastLoginDate: parsed.lastLoginDate || null,
      loginStreak: parsed.loginStreak || 0,
      streakRewardsClaimed: parsed.streakRewardsClaimed || [],
      spfRules: parsed.spfRules || SPF_RULES.reduce((acc, rule) => ({...acc, [rule.id]: rule.default}), {}),
      spfExposures: parsed.spfExposures || {},
      spfMetrics: parsed.spfMetrics || {},
      spfPolicy: parsed.spfPolicy || null,
      worryWindow: parsed.worryWindow || []
    };
  } catch {
    return { 
      entries: [], 
      darkMode: true, 
      gameFaces: [], 
      dailyPositives: {}, 
      risksTaken: [],
      spfRules: SPF_RULES.reduce((acc, rule) => ({...acc, [rule.id]: rule.default}), {}),
      spfExposures: {},
      spfMetrics: {},
      worryWindow: []
    };
  }
}

function saveState(state) {
  localStorage.setItem(STORAGE_KEY, JSON.stringify(state));
}

function useLocalState() {
  const [state, setState] = useState(loadState());
  useEffect(() => saveState(state), [state]);
  return [state, setState];
}

function calcXP(entries, risksTaken = [], streakBonuses = []) {
  let xp = 0;
  entries.forEach(e => {
    if (e.attempted) {
      xp += 10; // Base XP
      xp += (e.intensity || 0) * 3; // Intensity bonus
      if (e.usedControllers) xp += 5; // Controller bonus
      if (e.gameFace) xp += 5; // Game Face bonus
    }
  });

  // Add risk-taking XP
  risksTaken.forEach(risk => {
    const scenario = RISK_SCENARIOS.find(s => s.id === risk.scenarioId);
    if (scenario) xp += scenario.xp;
  });

  // Add streak bonuses
  streakBonuses.forEach(bonus => {
    const reward = STREAK_REWARDS.find(r => r.days === bonus);
    if (reward) xp += reward.bonus;
  });

  return xp;
}

function getRank(xp) {
  let current = RANKS[0];
  for (const r of RANKS) if (xp >= r.xp) current = r;
  const currentIndex = RANKS.indexOf(current);
  const next = RANKS[currentIndex + 1];
  const toNext = next ? next.xp - xp : 0;
  const progress = next ? ((xp - current.xp) / (next.xp - current.xp)) * 100 : 100;
  return { current, next, toNext, progress };
}

function calcStreak(entries) {
  if (!entries.length) return 0;
  const byDay = new Map();
  for (const e of entries) {
    if (!e.attempted) continue;
    byDay.set(e.date, true);
  }
  let streak = 0;
  let cursor = todayISO();
  while (byDay.get(cursor)) {
    streak += 1;
    const d = new Date(cursor + "T00:00:00");
    d.setDate(d.getDate() - 1);
    cursor = todayISO(d);
  }
  return streak;
}

export default function App() {
  const [state, setState] = useLocalState();
  const [view, setView] = useState("log");
  const [selectedAnt, setSelectedAnt] = useState(0);
  const [intensity, setIntensity] = useState(3);
  const [notes, setNotes] = useState("");
  const [performanceContext, setPerformanceContext] = useState("");
  const [usedControllers, setUsedControllers] = useState({ selfTalk: [], body: [] });
  const [selectedGameFace, setSelectedGameFace] = useState(null);
  const [todaysPositive, setTodaysPositive] = useState(state.dailyPositives[todayISO()] || "");
  const [rouletteWord, setRouletteWord] = useState(null);
  const [isSpinning, setIsSpinning] = useState(false);
  const [showStreakReward, setShowStreakReward] = useState(null);

  // SPF State
  const [selectedExposures, setSelectedExposures] = useState([]);
  const [selectedScript, setSelectedScript] = useState(null);
  const [copiedScript, setCopiedScript] = useState(null);
  const [worryText, setWorryText] = useState("");
  const [worryTimerActive, setWorryTimerActive] = useState(false);
  const [spfEvidence, setSpfEvidence] = useState("");
  const [policyForm, setPolicyForm] = useState({
    willDo: "",
    wontDo: "",
    exitDate: "",
    noticeDate: ""
  });

  // Game Face Creation
  const [newGameFaceName, setNewGameFaceName] = useState("");
  const [selectedWords, setSelectedWords] = useState([]);
  const [modelPerson, setModelPerson] = useState("");
  const [gameFaceSource, setGameFaceSource] = useState("memory");

  const entries = state.entries || [];
  const gameFaces = state.gameFaces || [];
  const risksTaken = state.risksTaken || [];
  const darkMode = state.darkMode ?? true;
  const todays = entries.filter((e) => e.date === todayISO());
  const loginStreak = state.loginStreak || 0;
  const streakRewardsClaimed = state.streakRewardsClaimed || [];

  const xp = useMemo(() => calcXP(entries, risksTaken, streakRewardsClaimed), [entries, risksTaken, streakRewardsClaimed]);
  const { current: rank, next, toNext, progress } = useMemo(() => getRank(xp), [xp]);
  const performanceStreak = useMemo(() => calcStreak(entries), [entries]);

  // Check and update login streak
  useEffect(() => {
    const today = todayISO();
    const lastLogin = state.lastLoginDate;

    if (lastLogin !== today) {
      const yesterday = new Date();
      yesterday.setDate(yesterday.getDate() - 1);
      const yesterdayISO = todayISO(yesterday);

      let newStreak = loginStreak;
      if (lastLogin === yesterdayISO) {
        newStreak = loginStreak + 1;
      } else if (lastLogin !== today) {
        newStreak = 1;
      }

      setState(s => ({
        ...s,
        lastLoginDate: today,
        loginStreak: newStreak
      }));

      // Check for unclaimed streak rewards
      const unclaimedReward = STREAK_REWARDS.find(
        r => r.days === newStreak && !streakRewardsClaimed.includes(r.days)
      );
      if (unclaimedReward) {
        setShowStreakReward(unclaimedReward);
      }
    }
  }, []);

  function claimStreakReward(reward) {
    setState(s => ({
      ...s,
      streakRewardsClaimed: [...s.streakRewardsClaimed, reward.days]
    }));
    setShowStreakReward(null);
  }

  // SPF Functions
  function toggleSPFRule(ruleId) {
    setState(s => ({
      ...s,
      spfRules: {
        ...s.spfRules,
        [ruleId]: !s.spfRules[ruleId]
      }
    }));
  }

  function copyScript(script) {
    navigator.clipboard.writeText(script.text);
    setCopiedScript(script.id);
    setTimeout(() => setCopiedScript(null), 2000);
  }

  function logSPFExposure(exposureId) {
    const today = todayISO();
    setState(s => ({
      ...s,
      spfExposures: {
        ...s.spfExposures,
        [today]: [...(s.spfExposures[today] || []), exposureId]
      }
    }));
  }

  function logSPFMetric(metricId, score, evidence = "") {
    const today = todayISO();
    setState(s => ({
      ...s,
      spfMetrics: {
        ...s.spfMetrics,
        [today]: {
          ...s.spfMetrics[today],
          [metricId]: { score, evidence, ts: Date.now() }
        }
      }
    }));
  }

  function saveWorry() {
    if (worryText.trim()) {
      setState(s => ({
        ...s,
        worryWindow: [...s.worryWindow, {
          text: worryText.trim(),
          date: todayISO(),
          ts: Date.now()
        }]
      }));
      setWorryText("");
    }
  }

  function generatePolicy() {
    const policy = {
      ...policyForm,
      scripts: [selectedScript],
      generated: Date.now()
    };
    setState(s => ({ ...s, spfPolicy: policy }));
  }

  const todaysExposures = state.spfExposures[todayISO()] || [];
  const todaysMetrics = state.spfMetrics[todayISO()] || {};

  function toggleDarkMode() {
    setState(s => ({ ...s, darkMode: !s.darkMode }));
  }

  function saveDailyPositive() {
    if (todaysPositive.trim()) {
      setState(s => ({
        ...s,
        dailyPositives: {
          ...s.dailyPositives,
          [todayISO()]: todaysPositive.trim()
        }
      }));
    }
  }

  function spinPowerWordRoulette() {
    setIsSpinning(true);
    const allWords = GAME_FACE_WORDS.flat();
    let counter = 0;
    const interval = setInterval(() => {
      setRouletteWord(allWords[Math.floor(Math.random() * allWords.length)]);
      counter++;
      if (counter > 20) {
        clearInterval(interval);
        setIsSpinning(false);
      }
    }, 100);
  }

  function logRiskTaken(scenarioId) {
    const risk = {
      id: crypto.randomUUID(),
      date: todayISO(),
      ts: Date.now(),
      scenarioId
    };
    setState(s => ({ ...s, risksTaken: [risk, ...(s.risksTaken || [])] }));
  }

  function addEntry(attempted) {
    const entry = {
      id: crypto.randomUUID(),
      date: todayISO(),
      ts: Date.now(),
      ant: ANTS[selectedAnt].name,
      intensity: Number(intensity),
      attempted: Boolean(attempted),
      notes: notes.trim(),
      performanceContext: performanceContext.trim(),
      usedControllers: usedControllers,
      gameFace: selectedGameFace,
      antSquashed: attempted && (usedControllers.selfTalk.length > 0 || usedControllers.body.length > 0)
    };
    setState((s) => ({ ...s, entries: [entry, ...(s.entries || [])] }));

    // Reset form
    setNotes("");
    setPerformanceContext("");
    setUsedControllers({ selfTalk: [], body: [] });
    setSelectedGameFace(null);
  }

  function createGameFace() {
    if (!newGameFaceName || selectedWords.length === 0) return;

    const gameFace = {
      id: crypto.randomUUID(),
      name: newGameFaceName,
      words: selectedWords,
      modelPerson: modelPerson,
      source: gameFaceSource,
      created: Date.now()
    };

    setState(s => ({ ...s, gameFaces: [...(s.gameFaces || []), gameFace] }));

    // Reset game face form
    setNewGameFaceName("");
    setSelectedWords([]);
    setModelPerson("");
    setView("log");
  }

  function toggleController(type, technique) {
    setUsedControllers(prev => ({
      ...prev,
      [type]: prev[type].includes(technique) 
        ? prev[type].filter(t => t !== technique)
        : [...prev[type], technique]
    }));
  }

  const performanceMindsetLevel = useMemo(() => {
    const recent = entries.slice(0, 10).filter(e => e.attempted);
    if (recent.length === 0) return 5;

    const avgIntensity = recent.reduce((acc, e) => acc + e.intensity, 0) / recent.length;
    const squashRate = recent.filter(e => e.antSquashed).length / recent.length;
    const gameFaceRate = recent.filter(e => e.gameFace).length / recent.length;

    let level = 5;
    if (squashRate > 0.7) level += 2;
    else if (squashRate > 0.4) level += 1;

    if (gameFaceRate > 0.5) level += 1;
    if (avgIntensity < 3) level += 1;

    return Math.min(10, Math.max(1, level));
  }, [entries]);

  const todaysRisks = risksTaken.filter(r => r.date === todayISO());

  return (
    <div className={`min-h-screen transition-all duration-500 ${darkMode 
      ? 'bg-gradient-to-br from-gray-900 via-purple-900 to-gray-900 text-white' 
      : 'bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50 text-gray-900'}`}>

      {/* Animated background */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className={`absolute -top-40 -right-40 w-80 h-80 ${darkMode ? 'bg-purple-500' : 'bg-purple-300'} rounded-full mix-blend-multiply filter blur-3xl opacity-20 animate-blob`}></div>
        <div className={`absolute -bottom-40 -left-40 w-80 h-80 ${darkMode ? 'bg-pink-500' : 'bg-pink-300'} rounded-full mix-blend-multiply filter blur-3xl opacity-20 animate-blob animation-delay-2000`}></div>
      </div>

      <div className="relative z-10 p-4 md:p-8 max-w-7xl mx-auto">
        {/* Header */}
        <header className="mb-8">
          <div className="flex justify-between items-start mb-6">
            <div className="space-y-2">
              <h1 className="text-4xl md:text-5xl font-black bg-gradient-to-r from-purple-400 via-pink-400 to-purple-400 bg-clip-text text-transparent">
                HPM TRACKER
              </h1>
              <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                Elite Competitor Model ‚Ä¢ Squash ANTs ‚Ä¢ Deploy Game Face ‚Ä¢ Take Risks
              </p>
            </div>
            <button
