        {/* Main Content */}
        {view === "live" && (
          <LiveView
            selectedAnt={selectedAnt}
            setSelectedAnt={setSelectedAnt}
            intensity={intensity}
            setIntensity={setIntensity}
            notes={notes}
            setNotes={setNotes}
            usedControllers={usedControllers}
            toggleController={toggleController}
            selectedGameFace={selectedGameFace}
            setSelectedGameFace={setSelectedGameFace}
            gameFaces={gameFaces}
            addEntry={addEntry}
            todays={todays}
            darkMode={darkMode}
            copyScript={copyScript}
            copiedScript={copiedScript}
            rouletteWord={rouletteWord}
            spfRules={state.spfRules}
          />
        )}

        {view === "log" && (
          <LogView
            selectedAnt={selectedAnt}
            setSelectedAnt={setSelectedAnt}
            intensity={intensity}
            setIntensity={setIntensity}
            notes={notes}
            setNotes={setNotes}
            performanceContext={performanceContext}
            setPerformanceContext={setPerformanceContext}
            usedControllers={usedControllers}
            toggleController={toggleController}
            selectedGameFace={selectedGameFace}
            setSelectedGameFace={setSelectedGameFace}
            gameFaces={gameFaces}
            addEntry={addEntry}
            darkMode={darkMode}
          />
        )}

        {view === "gameface" && (
          <GameFaceView
            gameFaces={gameFaces}
            setState={setState}
            darkMode={darkMode}
            setView={setView}
          />
        )}

        {view === "risks" && (
          <RisksView
