            </div>
          </div>
        </div>

        {/* Major Flows Grid */}
        <div className="mb-6">
          <h4 className="font-medium text-gray-700 mb-3">Major Nutrient Pathways</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {flowAnalysis.flows.slice(0, 9).map((flow, index) => {
