    { from: 'field_3', to: 'feed_silage', type: 'harvest', nutrients: { N: 2500, P: 400, K: 2000, S: 150 } },
    { from: 'feed_silage', to: 'livestock_high', type: 'feeding', nutrients: { N: 2000, P: 320, K: 1600, S: 120 } },
    { from: 'feed_concentrate', to: 'livestock_high', type: 'feeding', nutrients: { N: 700, P: 130, K: 90, S: 18 } },
    { from: 'livestock_high', to: 'output_milk', type: 'sale', nutrients: { N: 945, P: 158, K: 210, S: 30 } },
    { from: 'livestock_high', to: 'manure_slurry', type: 'manure_production', nutrients: { N: 1505, P: 242, K: 1295, S: 90 } },
    { from: 'manure_slurry', to: 'loss_atmospheric', type: 'atmospheric_loss', nutrients: { N: 301, P: 0, K: 0, S: 0 } },
    { from: 'manure_slurry', to: 'field_3', type: 'manure_application', nutrients: { N: 963, P: 194, K: 1036, S: 72 } },
    { from: 'external_fertiliser', to: 'field_1', type: 'fertilizer_application', nutrients: { N: 500, P: 50, K: 50, S: 10 } },
    { from: 'external_fertiliser', to: 'field_2', type: 'fertilizer_application', nutrients: { N: 400, P: 0, K: 100, S: 5 } },
    { from: 'field_1', to: 'loss_leaching', type: 'leaching_loss', nutrients: { N: 75, P: 5, K: 10, S: 2 } },
    { from: 'field_3', to: 'loss_leaching', type: 'leaching_loss', nutrients: { N: 144, P: 19, K: 52, S: 7 } },
  ]
};

const FERTILISER_PRICES = {
    N: 0.85, // ¬£ per kg
    P: 1.50, // ¬£ per kg
    K: 0.70, // ¬£ per kg
};

// --- HELPER FUNCTIONS ---

const calculateKOUBalance = (kouId, pathways) => {
    const balance = { N: 0, P: 0, K: 0, S: 0 };
    pathways.forEach(p => {
        if (p.to === kouId) {
            Object.keys(balance).forEach(key => balance[key] += p.nutrients[key] || 0);
        }
        if (p.from === kouId) {
            Object.keys(balance).forEach(key => balance[key] -= p.nutrients[key] || 0);
        }
    });
    return balance;
};

const getFieldStatus = (balance, area) => {
    const n_balance_per_ha = balance.N / area;
    if (n_balance_per_ha > 20) return { label: 'High Surplus', color: 'bg-red-500', textColor: 'text-red-500' };
    if (n_balance_per_ha < -20) return { label: 'Deficit', color: 'bg-blue-500', textColor: 'text-blue-500' };
    return { label: 'Optimal', color: 'bg-green-500', textColor: 'text-green-500' };
};

const formatNumber = (num) => Math.round(num).toLocaleString();
const formatCurrency = (num) => `¬£${formatNumber(num)}`;

// --- UI COMPONENTS ---

const KOU_ICONS = {
    [KOU_TYPES.FIELD]: <MapIcon className="h-5 w-5 text-green-500" />,
    [KOU_TYPES.LIVESTOCK_GROUP]: <Milk className="h-5 w-5 text-gray-500" />,
    [KOU_TYPES.FEED_STORE]: <Warehouse className="h-5 w-5 text-yellow-600" />,
    [KOU_TYPES.MANURE_STORE]: <Droplet className="h-5 w-5 text-amber-700" />,
    [KOU_TYPES.OUTPUT]: <Truck className="h-5 w-5 text-blue-500" />,
    [KOU_TYPES.EXTERNAL]: <ExternalLink className="h-5 w-5 text-indigo-500" />,
};

const NutrientPill = ({ nutrient, value, unit = 'kg' }) => {
    const colors = {
        N: 'bg-blue-100 text-blue-800',
        P: 'bg-purple-100 text-purple-800',
        K: 'bg-yellow-100 text-yellow-800',
        S: 'bg-red-100 text-red-800',
    };
    return (
        <div className={`text-xs font-medium px-2.5 py-1 rounded-full ${colors[nutrient]}`}>
            {nutrient}: {formatNumber(value)} {unit}
        </div>
    );
};

const Card = ({ children, className = '' }) => (
    <div className={`bg-white rounded-xl shadow-md p-6 ${className}`}>
        {children}
    </div>
);

const CardHeader = ({ title, icon, subtitle }) => (
    <div className="flex items-start justify-between mb-4">
        <div>
            <div className="flex items-center gap-3">
                {icon}
                <h2 className="text-xl font-bold text-gray-800">{title}</h2>
            </div>
            {subtitle && <p className="text-sm text-gray-500 mt-1">{subtitle}</p>}
        </div>
    </div>
);


const FinancialOptimisationModule = ({ farmData }) => {
    const slurryStore = Object.values(farmData.kous).find(k => k.id === 'manure_slurry');
    const slurryBalance = calculateKOUBalance(slurryStore.id, farmData.pathways);

    const fertiliserValue = useMemo(() => {
        const n_val = (slurryBalance.N > 0 ? slurryBalance.N : 0) * FERTILISER_PRICES.N;
        const p_val = (slurryBalance.P > 0 ? slurryBalance.P : 0) * FERTILISER_PRICES.P;
        const k_val = (slurryBalance.K > 0 ? slurryBalance.K : 0) * FERTILISER_PRICES.K;
        return n_val + p_val + k_val;
    }, [slurryBalance]);

    const costOfInefficiency = useMemo(() => {
        const field1Pathways = farmData.pathways.filter(p => p.to === 'field_1' && p.from === 'external_fertiliser');
        const field1 = farmData.kous['field_1'];
        if (field1.properties.p_index >= 3) {
            const wastedP = field1Pathways.reduce((sum, p) => sum + p.nutrients.P, 0);
            return wastedP * FERTILISER_PRICES.P;
        }
        return 0;
    }, [farmData]);

    return (
        <Card>
            <CardHeader title="Financial Optimisation" icon={<DollarSign className="h-6 w-6 text-green-600" />} subtitle="Translating nutrient data into profit" />
            <div className="space-y-4">
                <div className="bg-green-50 border-l-4 border-green-500 p-4 rounded-r-lg">
                    <div className="flex">
                        <div className="flex-shrink-0">
                            <Leaf className="h-5 w-5 text-green-500" />
                        </div>
                        <div className="ml-3">
                            <p className="text-sm font-semibold text-green-800">Fertiliser Replacement Value</p>
                            <p className="mt-1 text-2xl font-bold text-green-700">{formatCurrency(fertiliserValue)}</p>
                            <p className="text-xs text-gray-600">This is the value of the nutrients currently in your slurry lagoon if you had to buy them in a bag.</p>
                        </div>
                    </div>
                </div>
                {costOfInefficiency > 0 &&
                    <div className="bg-red-50 border-l-4 border-red-500 p-4 rounded-r-lg">
                        <div className="flex">
                            <div className="flex-shrink-0">
                                <Info className="h-5 w-5 text-red-500" />
                            </div>
                            <div className="ml-3">
                                <p className="text-sm font-semibold text-red-800">Cost of Inefficiency</p>
                                <p className="mt-1 text-2xl font-bold text-red-700">{formatCurrency(costOfInefficiency)}</p>
                                <p className="text-xs text-gray-600">You applied expensive Phosphorus to Long Meadow (P Index 3), costing you money without agronomic benefit.</p>
                            </div>
                        </div>
                    </div>
                }
                <div className="bg-blue-50 border-l-4 border-blue-500 p-4 rounded-r-lg">
                    <div className="flex">
                        <div className="flex-shrink-0">
                            <Tractor className="h-5 w-5 text-blue-500" />
                        </div>
                        <div className="ml-3">
                            <p className="text-sm font-semibold text-blue-800">Feed Cost Savings Potential</p>
                            <p className="mt-1 text-2xl font-bold text-blue-700">{formatCurrency(12000)}</p>
                            <p className="text-xs text-gray-600">Simulations show better slurry use on 'Silage Field' could improve forage quality, saving on purchased feed.</p>
                        </div>
                    </div>
                </div>
            </div>
        </Card>
    );
};

const FarmNutrientMap = ({ kous, pathways, onSelectKOU }) => {
    const fields = Object.values(kous).filter(k => k.type === KOU_TYPES.FIELD);

    const getFieldPosition = (index, total) => {
        const cols = Math.ceil(Math.sqrt(total));
        const row = Math.floor(index / cols);
        const col = index % cols;
        return { gridRowStart: row + 1, gridColumnStart: col + 1 };
    };

    return (
        <Card>
            <CardHeader title="Farm Nutrient Map" icon={<MapIcon className="h-6 w-6 text-green-600" />} subtitle="Visualise nutrient status across your fields" />
            <div className={`grid grid-cols-3 gap-4`}>
                {fields.map((field, index) => {
                    const balance = calculateKOUBalance(field.id, pathways);
                    const status = getFieldStatus(balance, field.properties.area);
                    return (
                        <div key={field.id} onClick={() => onSelectKOU(field)}
                            style={getFieldPosition(index, fields.length)}
                            className="p-4 border rounded-lg hover:bg-gray-50 cursor-pointer transition-all duration-200">
                            <div className="flex justify-between items-center">
                                <p className="font-bold text-gray-700">{field.name}</p>
                                <div className={`h-3 w-3 rounded-full ${status.color}`}></div>
                            </div>
                            <p className="text-sm text-gray-500">{field.properties.area} ha</p>
                            <div className="mt-2 flex flex-wrap gap-1">
                                <NutrientPill nutrient="N" value={balance.N} />
                                <NutrientPill nutrient="P" value={balance.P} />
                                <NutrientPill nutrient="K" value={balance.K} />
                            </div>
                        </div>
                    );
                })}
            </div>
        </Card>
    );
};

// IMPORTANT: Recharts Sankey is limited. For a true, multi-level Sankey, a library like D3.js would be required.
// This is a simplified flow diagram using Sankey's structure.
const NutrientSankeyDiagram = ({ kous, pathways, selectedNutrient }) => {
    const { nodes, links } = useMemo(() => {
        const nodeMap = new Map();
        Object.values(kous).forEach((kou, i) => nodeMap.set(kou.id, { name: kou.name, type: kou.type }));

        const nodes = Array.from(nodeMap.entries()).map(([id, data]) => ({ name: data.name, type: data.type }));

        const links = pathways
            // FIX: Filter out circular dependencies for visualization to prevent crash
            .filter(p => !(p.from === 'manure_slurry' && p.to.startsWith('field')))
            .map(p => ({
                source: nodes.findIndex(n => n.name === nodeMap.get(p.from)?.name),
                target: nodes.findIndex(n => n.name === nodeMap.get(p.to)?.name),
                value: p.nutrients[selectedNutrient] || 0,
            })).filter(l => l.value > 0 && l.source !== -1 && l.target !== -1);

        return { nodes, links };
    }, [kous, pathways, selectedNutrient]);

    const nodeColors = {
      [KOU_TYPES.FIELD]: '#22c55e',
      [KOU_TYPES.LIVESTOCK_GROUP]: '#71717a',
      [KOU_TYPES.FEED_STORE]: '#d97706',
      [KOU_TYPES.MANURE_STORE]: '#a16207',
      [KOU_TYPES.OUTPUT]: '#3b82f6',
      [KOU_TYPES.EXTERNAL]: '#6366f1',
    };

    const CustomNode = ({ x, y, width, height, index, payload }) => {
        const nodeData = nodes[index];
        if (!nodeData) return null;
        return (
            <Layer key={`custom-node-${index}`}>
                <Rectangle
                    x={x}
                    y={y}
                    width={width}
                    height={height}
                    fill={nodeColors[nodeData.type]}
                    fillOpacity="1"
                />
                <text
                    textAnchor={x > width ? 'end' : 'start'}
                    x={x > width ? x - 6 : x + 6}
                    y={y + height / 2}
                    fontSize="14"
                    stroke="#333"
                >
                    {nodeData.name}
                </text>
            </Layer>
        );
    };

    return (
        <Card>
            <CardHeader title="Nutrient Flow" icon={<ChevronsRight className="h-6 w-6 text-blue-600" />} subtitle={`Visualising the flow of ${selectedNutrient} through the farm`} />
            <div style={{ width: '100%', height: 500 }}>
                <ResponsiveContainer>
                    <Sankey
                        data={{ nodes, links }}
                        node={CustomNode}
                        nodePadding={50}
                        margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
                        link={{ stroke: '#60a5fa', strokeOpacity: 0.5 }}
                    >
                        <Tooltip />
                    </Sankey>
                </ResponsiveContainer>
            </div>
        </Card>
    );
};

const ScenarioPlanning = ({ farmData, onApplyScenario }) => {
    const [scenarios, setScenarios] = useState([
        { id: 'base', name: 'Baseline', active: true, changes: [] },
        { id: 's1', name: 'Trailing Shoe Applicator', active: false, changes: [{ description: 'Reduce atmospheric N loss from slurry by 20%', impact: { kou: 'manure_slurry', nutrient: 'N', from: 'loss_atmospheric', percentage: -0.20 } }] },
        { id: 's2', name: 'Slurry Separator', active: false, changes: [{ description: 'Concentrate 50% of P into solid fraction', impact: {} }] },
    ]);

    const handleToggle = (id) => {
        const newScenarios = scenarios.map(s => ({...s, active: s.id === id}));
        setScenarios(newScenarios);
        onApplyScenario(newScenarios.find(s => s.active));
    };

    return (
        <Card>
            <CardHeader title="Scenario Planning" icon={<Sliders className="h-6 w-6 text-purple-600" />} subtitle="Model the impact of management changes" />
            <div className="space-y-3">
                {scenarios.map(scenario => (
                    <div key={scenario.id} onClick={() => handleToggle(scenario.id)}
                        className={`p-4 border rounded-lg cursor-pointer transition-all ${scenario.active ? 'bg-purple-50 border-purple-500 ring-2 ring-purple-200' : 'hover:bg-gray-50'}`}>
                        <div className="flex justify-between items-center">
                            <p className="font-bold text-gray-800">{scenario.name}</p>
                            <div className={`h-5 w-5 rounded-full border-2 ${scenario.active ? 'bg-purple-500 border-purple-500' : 'border-gray-300'}`} />
                        </div>
                        {scenario.changes.length > 0 && <p className="text-sm text-gray-600 mt-1">{scenario.changes[0].description}</p>}
                        {scenario.active && scenario.id === 's1' && (
                             <div className="mt-4 pt-4 border-t">
                                <p className="text-sm font-bold text-gray-700">Projected Financial Impact</p>
                                <div className="flex justify-between items-center mt-2">
                                    <p className="text-sm text-gray-600">Annual Fertiliser Saving:</p>
                                    <p className="text-sm font-bold text-green-600">{formatCurrency(2500)}</p>
                                </div>
                                <div className="flex justify-between items-center mt-1">
                                    <p className="text-sm text-gray-600">Estimated Investment:</p>
                                    <p className="text-sm font-bold text-red-600">{formatCurrency(15000)}</p>
                                </div>
                                <div className="flex justify-between items-center mt-1">
                                    <p className="text-sm text-gray-600">Return on Investment:</p>
                                    <p className="text-sm font-bold text-blue-600">~6 years</p>
                                </div>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </Card>
    );
};

const KOUDetailView = ({ kou, pathways, onBack }) => {
    if (!kou) return null;
    const balance = calculateKOUBalance(kou.id, pathways);
    const incoming = pathways.filter(p => p.to === kou.id);
    const outgoing = pathways.filter(p => p.from === kou.id);

    return (
        <Card className="col-span-1 md:col-span-2 lg:col-span-1">
            <div className="flex justify-between items-center">
                <CardHeader title={kou.name} icon={KOU_ICONS[kou.type]} />
