                <h3 className="font-bold text-gray-700 mb-2">Nutrient Balance</h3>
                <div className="flex flex-wrap gap-2 p-3 bg-gray-50 rounded-lg">
                    {Object.entries(balance).map(([key, value]) => <NutrientPill key={key} nutrient={key} value={value} />)}
