            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-6">
                <div>
                    <h3 className="font-bold text-gray-700 mb-2">Incoming Pathways</h3>
                    <div className="space-y-2">
                        {incoming.map((p, i) => (
                            <div key={i} className="text-sm p-2 bg-green-50 rounded">
