                    </div>
                </div>
                <div>
                    <h3 className="font-bold text-gray-700 mb-2">Outgoing Pathways</h3>
                    <div className="space-y-2">
                        {outgoing.map((p, i) => (
                            <div key={i} className="text-sm p-2 bg-red-50 rounded">
                                To: <span className="font-semibold">{MOCK_FARM_DATA.kous[p.to]?.name || p.to}</span>
                                <div className="flex flex-wrap gap-1 mt-1">
                                    {Object.entries(p.nutrients).map(([key, value]) => value > 0 && <NutrientPill key={key} nutrient={key} value={value} />)}
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </Card>
    );
};

// --- MAIN APP COMPONENT (Orchestrator) ---

export default function HighResolutionNutrientBudget() {
    const [farmData, setFarmData] = useState(MOCK_FARM_DATA);
    const [selectedKOU, setSelectedKOU] = useState(null);
    const [selectedNutrient, setSelectedNutrient] = useState('N');

    const handleApplyScenario = useCallback((scenario) => {
        let newPathways = [...MOCK_FARM_DATA.pathways];
        if(scenario.id === 's1' && scenario.changes.length > 0) {
            const change = scenario.changes[0];
            newPathways = newPathways.map(p => {
                if(p.from === change.impact.kou && p.to === change.impact.from) {
                    const originalValue = MOCK_FARM_DATA.pathways.find(op => op.from === p.from && op.to === p.to).nutrients[change.impact.nutrient];
                    return {
                        ...p,
                        nutrients: {
                            ...p.nutrients,
                            [change.impact.nutrient]: originalValue * (1 + change.impact.percentage)
                        }
                    };
                }
                return p;
            });
        } else {
          // Reset to baseline if another scenario is chosen
          newPathways = MOCK_FARM_DATA.pathways;
        }
        setFarmData(prev => ({...prev, pathways: newPathways}));
    }, []);

    const MainContent = () => {
        if (selectedKOU) {
            return <KOUDetailView kou={selectedKOU} pathways={farmData.pathways} onBack={() => setSelectedKOU(null)} />;
        }
        return (
            <>
                <FinancialOptimisationModule farmData={farmData} />
                <FarmNutrientMap kous={farmData.kous} pathways={farmData.pathways} onSelectKOU={setSelectedKOU} />
                <div className="col-span-1 md:col-span-2">
                    <ScenarioPlanning farmData={farmData} onApplyScenario={handleApplyScenario} />
                </div>
                <div className="col-span-1 md:col-span-2">
                    <div className="flex justify-end mb-4">
                        <div className="flex items-center bg-white p-1 rounded-lg shadow-sm border">
                            {['N', 'P', 'K', 'S'].map(n => (
                                <button key={n} onClick={() => setSelectedNutrient(n)}
                                    className={`px-3 py-1 text-sm font-semibold rounded-md transition-colors ${selectedNutrient === n ? 'bg-blue-600 text-white' : 'text-gray-600 hover:bg-gray-100'}`}>
                                    {n}
                                </button>
                            ))}
                        </div>
                    </div>
                    <NutrientSankeyDiagram kous={farmData.kous} pathways={farmData.pathways} selectedNutrient={selectedNutrient} />
                </div>
            </>
        );
    };

    return (
        <div className="min-h-screen bg-gray-50 font-sans">
            <header className="bg-white shadow-sm">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center">
                    <div className="flex items-center gap-3">
                        <Leaf className="h-8 w-8 text-green-600" />
                        <div>
                            <h1 className="text-2xl font-bold text-gray-900">The Mineral Loop‚Ñ¢</h1>
                            <p className="text-sm text-gray-500">High-Resolution Nutrient & Financial Audit</p>
                        </div>
                    </div>
                    <div className="flex items-center gap-4">
                        <button className="p-2 rounded-full hover:bg-gray-100 text-gray-500">
                            <HelpCircle className="h-5 w-5" />
                        </button>
                        <button className="p-2 rounded-full hover:bg-gray-100 text-gray-500">
                            <Settings className="h-5 w-5" />
                        </button>
                        <div className="flex items-center gap-2">
                            <img src="https://placehold.co/40x40/E2E8F0/4A5568?text=JD" alt="User" className="h-10 w-10 rounded-full" />
                            <div>
                                <p className="font-semibold text-sm text-gray-800">John Doe</p>
                                <p className="text-xs text-gray-500">Oakwood Dairy Farm</p>
                            </div>
                        </div>
                    </div>
                </div>
            </header>
            <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    <MainContent />
                </div>
            </main>
        </div>
    );
}
