        },
        importedManure: { amount: 0, nContent: 0, pContent: 0 }
      },
      outputs: {
        milk: { amount: 0, price: 0.35 },
        culls: { number: 0, weight: 0, price: 0 },
        calves: { number: 0, price: 0 },
        exportedManure: { amount: 0, nContent: 0, pContent: 0 },
        crops: { amount: 0, nContent: 0, pContent: 0 }
      },
      manure: {
        storage: 0,
        applied: 0,
        nContent: 3.0,
        pContent: 0.6
      }
    };
  };

  const [farmData, setFarmData] = useState(loadInitialData);
  const [activeTab, setActiveTab] = useState('inputs');
  const [errors, setErrors] = useState({});

  // Save data to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('nutrientBudgetData', JSON.stringify(farmData));
  }, [farmData]);

  // Update nested state helper
  const updateNestedState = (section, subsection, field, value) => {
    setFarmData(prev => ({
      ...prev,
      [section]: {
        ...prev[section],
        [subsection]: {
          ...prev[section][subsection],
          [field]: parseFloat(value) || 0
        }
      }
    }));
  };

  // Calculate nutrient balances
  const calculateNutrientBalance = () => {
    const { inputs, outputs, farmInfo, manure } = farmData;

    // N Inputs (kg)
    const feedN = (inputs.purchasedFeed.concentrates.amount * inputs.purchasedFeed.concentrates.nContent * 10) +
                  (inputs.purchasedFeed.forages.amount * inputs.purchasedFeed.forages.nContent * 10);
    const fertilizerN = inputs.fertilizer.nitrogen.amount;
    const importedManureN = inputs.importedManure.amount * inputs.importedManure.nContent;
    const atmosphericN = farmInfo.totalArea * 20; // Assumed atmospheric deposition
    const totalNInputs = feedN + fertilizerN + importedManureN + atmosphericN;

    // N Outputs (kg)
    const milkN = outputs.milk.amount * 0.0053; // 5.3 kg N per tonne milk
    const meatN = (outputs.culls.number * outputs.culls.weight * 0.025); // 2.5% N in meat
    const exportedManureN = outputs.exportedManure.amount * outputs.exportedManure.nContent;
    const cropN = outputs.crops.amount * outputs.crops.nContent;
    const totalNOutputs = milkN + meatN + exportedManureN + cropN;

    // P calculations similar to N
    const feedP = (inputs.purchasedFeed.concentrates.amount * inputs.purchasedFeed.concentrates.pContent * 10) +
                  (inputs.purchasedFeed.forages.amount * inputs.purchasedFeed.forages.pContent * 10);
    const fertilizerP = inputs.fertilizer.phosphorus.amount;
    const importedManureP = inputs.importedManure.amount * inputs.importedManure.pContent;
    const totalPInputs = feedP + fertilizerP + importedManureP;

    const milkP = outputs.milk.amount * 0.001; // 1 kg P per tonne milk
    const meatP = (outputs.culls.number * outputs.culls.weight * 0.007); // 0.7% P in meat
    const exportedManureP = outputs.exportedManure.amount * outputs.exportedManure.pContent;
    const cropP = outputs.crops.amount * outputs.crops.pContent;
    const totalPOutputs = milkP + meatP + exportedManureP + cropP;

    // Farm balance
    const nBalance = totalNInputs - totalNOutputs;
    const pBalance = totalPInputs - totalPOutputs;
    const nEfficiency = totalNOutputs > 0 ? (totalNOutputs / totalNInputs) * 100 : 0;
    const pEfficiency = totalPOutputs > 0 ? (totalPOutputs / totalPInputs) * 100 : 0;

    // Per hectare calculations
    const nBalancePerHa = farmInfo.totalArea > 0 ? nBalance / farmInfo.totalArea : 0;
    const pBalancePerHa = farmInfo.totalArea > 0 ? pBalance / farmInfo.totalArea : 0;

    // Manure production estimate
    const estimatedManureN = (farmInfo.milkingCows * 100) + (farmInfo.youngstock * 40); // kg N/year
    const manureAppliedN = manure.applied * manure.nContent;

    return {
      nitrogen: {
        inputs: { feed: feedN, fertilizer: fertilizerN, imported: importedManureN, atmospheric: atmosphericN, total: totalNInputs },
        outputs: { milk: milkN, meat: meatN, exported: exportedManureN, crops: cropN, total: totalNOutputs },
        balance: nBalance,
        balancePerHa: nBalancePerHa,
        efficiency: nEfficiency,
        manureProduced: estimatedManureN,
        manureApplied: manureAppliedN
      },
      phosphorus: {
        inputs: { feed: feedP, fertilizer: fertilizerP, imported: importedManureP, total: totalPInputs },
        outputs: { milk: milkP, meat: meatP, exported: exportedManureP, crops: cropP, total: totalPOutputs },
        balance: pBalance,
        balancePerHa: pBalancePerHa,
        efficiency: pEfficiency
      }
    };
  };

  const nutrientBalance = calculateNutrientBalance();

  // Check NVZ compliance
  const nvzLimit = 170; // kg N/ha
  const isNVZCompliant = nutrientBalance.nitrogen.balancePerHa <= nvzLimit;

  // Export data
  const exportData = () => {
    const exportObj = {
      timestamp: new Date().toISOString(),
      farmData,
      nutrientBalance,
      nvzCompliance: {
        limit: nvzLimit,
        actual: nutrientBalance.nitrogen.balancePerHa,
        compliant: isNVZCompliant
      }
    };

    const dataStr = JSON.stringify(exportObj, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);

    const exportFileDefaultName = `nutrient-budget-${farmData.farmInfo.name || 'farm'}-${new Date().toISOString().split('T')[0]}.json`;

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  };

  // Import data
  const importData = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const imported = JSON.parse(e.target.result);
          if (imported.farmData) {
            setFarmData(imported.farmData);
          }
        } catch (error) {
          alert('Error importing file. Please check the file format.');
        }
      };
      reader.readAsText(file);
    }
  };

  // Charts data
  const balanceChartData = [
    { name: 'Nitrogen', inputs: nutrientBalance.nitrogen.inputs.total, outputs: nutrientBalance.nitrogen.outputs.total, balance: nutrientBalance.nitrogen.balance },
    { name: 'Phosphorus', inputs: nutrientBalance.phosphorus.inputs.total, outputs: nutrientBalance.phosphorus.outputs.total, balance: nutrientBalance.phosphorus.balance }
  ];

  const efficiencyData = [
    { name: 'N Efficiency', value: nutrientBalance.nitrogen.efficiency, fill: '#3b82f6' },
    { name: 'P Efficiency', value: nutrientBalance.phosphorus.efficiency, fill: '#10b981' }
  ];

  const nInputsPieData = [
    { name: 'Feed', value: nutrientBalance.nitrogen.inputs.feed, fill: '#3b82f6' },
    { name: 'Fertilizer', value: nutrientBalance.nitrogen.inputs.fertilizer, fill: '#10b981' },
    { name: 'Imported Manure', value: nutrientBalance.nitrogen.inputs.imported, fill: '#f59e0b' },
    { name: 'Atmospheric', value: nutrientBalance.nitrogen.inputs.atmospheric, fill: '#6366f1' }
  ].filter(item => item.value > 0);

  const nOutputsPieData = [
    { name: 'Milk', value: nutrientBalance.nitrogen.outputs.milk, fill: '#3b82f6' },
    { name: 'Meat', value: nutrientBalance.nitrogen.outputs.meat, fill: '#10b981' },
    { name: 'Exported Manure', value: nutrientBalance.nitrogen.outputs.exported, fill: '#f59e0b' },
    { name: 'Crops', value: nutrientBalance.nitrogen.outputs.crops, fill: '#6366f1' }
  ].filter(item => item.value > 0);

  return (
