              <p className="text-gray-600 mt-1">Simple tool for dairy farm nutrient management planning</p>
            </div>
            <div className="flex gap-2">
              <label className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 cursor-pointer flex items-center gap-2">
                <Upload className="w-4 h-4" />
                Import
                <input type="file" onChange={importData} className="hidden" accept=".json" />
              </label>
              <button
                onClick={exportData}
                className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 flex items-center gap-2"
              >
                <Download className="w-4 h-4" />
                Export
              </button>
            </div>
          </div>
        </div>

        {/* NVZ Compliance Alert */}
        <div className={`mb-6 p-4 rounded-lg flex items-center gap-3 ${
          isNVZCompliant ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
        }`}>
          {isNVZCompliant ? (
            <CheckCircle className="w-6 h-6" />
          ) : (
            <AlertTriangle className="w-6 h-6" />
          )}
          <div className="flex-1">
            <p className="font-semibold">
              NVZ Compliance Status: {isNVZCompliant ? 'Compliant' : 'Non-Compliant'}
            </p>
            <p className="text-sm">
              Current N balance: {nutrientBalance.nitrogen.balancePerHa.toFixed(1)} kg/ha 
              (Limit: {nvzLimit} kg/ha)
            </p>
          </div>
        </div>

        {/* Quick Stats */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
