// Key Operational Unit (KOU) Types
export const KOU_TYPES = {
  FIELD: 'field',
  LIVESTOCK_GROUP: 'livestock_group',
  FEED_STORE: 'feed_store',
  MANURE_STORE: 'manure_store',
  OUTPUT: 'output',
  EXTERNAL: 'external',
};

// Field use types
export const FIELD_USE_TYPES = {
  GRAZING_PLATFORM: 'grazing_platform',
  SILAGE_GROUND: 'silage_ground',
  ARABLE: 'arable',
  MAIZE: 'maize',
  PERMANENT_PASTURE: 'permanent_pasture',
};

// Livestock group types
export const LIVESTOCK_GROUPS = {
  MILKING_HIGH: 'milking_high_yielders',
  MILKING_MID: 'milking_mid_yielders',
  MILKING_LOW: 'milking_low_yielders',
  DRY_COWS: 'dry_cows',
  YOUNGSTOCK_0_6: 'youngstock_0_6_months',
  YOUNGSTOCK_6_12: 'youngstock_6_12_months',
  YOUNGSTOCK_12_24: 'youngstock_12_24_months',
};

// Feed store types
export const FEED_STORES = {
  GRASS_SILAGE_CLAMP_1: 'grass_silage_clamp_1',
  GRASS_SILAGE_CLAMP_2: 'grass_silage_clamp_2',
  MAIZE_SILAGE_CLAMP: 'maize_silage_clamp',
  CONCENTRATE_STORE: 'concentrate_store',
  STRAW_BARN: 'straw_barn',
};

// Manure store types
export const MANURE_STORES = {
  SLURRY_LAGOON_1: 'slurry_lagoon_1',
  SLURRY_LAGOON_2: 'slurry_lagoon_2',
  FYM_HEAP_1: 'fym_heap_1',
  FYM_HEAP_2: 'fym_heap_2',
  DIRTY_WATER_TANK: 'dirty_water_tank',
};

// Pathway types (nutrient transfer mechanisms)
export const PATHWAY_TYPES = {
  FEEDING: 'feeding',
  GRAZING: 'grazing',
  MANURE_PRODUCTION: 'manure_production',
  MANURE_APPLICATION: 'manure_application',
  FERTILIZER_APPLICATION: 'fertilizer_application',
  HARVEST: 'harvest',
  SALE: 'sale',
  PURCHASE: 'purchase',
  ATMOSPHERIC_LOSS: 'atmospheric_loss',
  LEACHING_LOSS: 'leaching_loss',
  RUNOFF_LOSS: 'runoff_loss',
};

// Nutrient prices for financial calculations
export const NUTRIENT_PRICES = {
  N: 0.85, // ¬£ per kg
  P: 1.50, // ¬£ per kg
  K: 0.70, // ¬£ per kg
  S: 0.50, // ¬£ per kg
};

// Create a new KOU
export const createKOU = (type, id, name, properties = {}) => ({
  id,
  type,
  name,
  properties: {
    ...properties,
    nutrients: {
      N: { total: 0, available: 0, organic: 0 },
      P: { total: 0, available: 0, index: 2 },
      K: { total: 0, available: 0, index: 2 },
      S: { total: 0, available: 0 }
    }
  },
  created: new Date().toISOString(),
  modified: new Date().toISOString()
});

// Create a pathway between KOUs
export const createPathway = (from, to, type, nutrients = {}, properties = {}) => ({
  id: `${from}_to_${to}_${Date.now()}`,
  from,
  to,
  type,
  // Nutrient transfer quantities (kg/year)
  nutrients: {
    N: nutrients.N || 0,
    P: nutrients.P || 0,
    K: nutrients.K || 0,
    S: nutrients.S || 0
  },
  // Transfer properties
  properties: {
    date: new Date().toISOString(),
    method: properties.method || '', // e.g., 'broadcast', 'injection', 'trailing_shoe'
    losses: {
      atmospheric: { N: 0, S: 0 }, // Ammonia, etc.
      leaching: { N: 0, P: 0, K: 0 },
      runoff: { N: 0, P: 0, K: 0 }
    },
    ...properties
  }
});

// Calculate nutrient balance for a specific KOU
export const calculateKOUBalance = (kou, pathways) => {
  const balance = {
    N: { inputs: 0, outputs: 0, balance: 0 },
    P: { inputs: 0, outputs: 0, balance: 0 },
    K: { inputs: 0, outputs: 0, balance: 0 },
    S: { inputs: 0, outputs: 0, balance: 0 }
  };

  // Sum inputs
  pathways
    .filter(p => p.to === kou.id)
    .forEach(pathway => {
      Object.keys(balance).forEach(nutrient => {
        balance[nutrient].inputs += pathway.nutrients[nutrient] || 0;
      });
    });

  // Sum outputs
  pathways
    .filter(p => p.from === kou.id)
    .forEach(pathway => {
      Object.keys(balance).forEach(nutrient => {
        balance[nutrient].outputs += pathway.nutrients[nutrient] || 0;
      });
    });

  // Calculate balance
  Object.keys(balance).forEach(nutrient => {
    balance[nutrient].balance = balance[nutrient].inputs - balance[nutrient].outputs;
  });

  return balance;
};

// Calculate farm-wide nutrient balance
export const calculateFarmBalance = (kous, pathways) => {
  const farmBalance = {
    N: { inputs: 0, outputs: 0, balance: 0, efficiency: 0 },
    P: { inputs: 0, outputs: 0, balance: 0, efficiency: 0 },
    K: { inputs: 0, outputs: 0, balance: 0, efficiency: 0 },
    S: { inputs: 0, outputs: 0, balance: 0, efficiency: 0 }
  };

  // Calculate total inputs (from external sources)
  pathways
    .filter(p => {
      const fromKOU = kous[p.from];
      return fromKOU && fromKOU.type === KOU_TYPES.EXTERNAL;
    })
    .forEach(pathway => {
      Object.keys(farmBalance).forEach(nutrient => {
        farmBalance[nutrient].inputs += pathway.nutrients[nutrient] || 0;
      });
    });

  // Add atmospheric deposition (simplified)
  const totalArea = Object.values(kous)
    .filter(k => k.type === KOU_TYPES.FIELD)
    .reduce((sum, field) => sum + (field.properties.area || 0), 0);

  farmBalance.N.inputs += totalArea * 20; // 20 kg N/ha atmospheric deposition

  // Calculate total outputs (sales and losses)
  pathways
    .filter(p => {
      const toKOU = kous[p.to];
      return toKOU && toKOU.type === KOU_TYPES.OUTPUT;
    })
    .forEach(pathway => {
      Object.keys(farmBalance).forEach(nutrient => {
        farmBalance[nutrient].outputs += pathway.nutrients[nutrient] || 0;
      });
    });

  // Calculate productive outputs (milk, meat, crops)
  const productiveOutputs = {
    N: 0, P: 0, K: 0, S: 0
  };

  pathways
    .filter(p => {
      const toKOU = kous[p.to];
      return toKOU && toKOU.type === KOU_TYPES.OUTPUT && 
             (p.type === PATHWAY_TYPES.SALE || toKOU.id.includes('milk') || toKOU.id.includes('livestock'));
    })
    .forEach(pathway => {
      Object.keys(productiveOutputs).forEach(nutrient => {
        productiveOutputs[nutrient] += pathway.nutrients[nutrient] || 0;
      });
    });

  // Calculate balances and efficiency
  Object.keys(farmBalance).forEach(nutrient => {
    farmBalance[nutrient].balance = farmBalance[nutrient].inputs - farmBalance[nutrient].outputs;
    farmBalance[nutrient].efficiency = farmBalance[nutrient].inputs > 0 
      ? (productiveOutputs[nutrient] / farmBalance[nutrient].inputs) * 100 
      : 0;
  });

  return farmBalance;
};

// Calculate financial value of nutrients
export const calculateNutrientValue = (nutrients) => {
  let totalValue = 0;
  Object.entries(nutrients).forEach(([nutrient, amount]) => {
    if (NUTRIENT_PRICES[nutrient] && amount > 0) {
      totalValue += amount * NUTRIENT_PRICES[nutrient];
    }
  });
  return totalValue;
};

// Get field nutrient status
export const getFieldStatus = (field, pathways) => {
  const balance = calculateKOUBalance(field, pathways);
  const nPerHa = field.properties.area > 0 ? balance.N.balance / field.properties.area : 0;

  let status = 'optimal';
  let statusColor = '#10b981'; // green
  let message = 'Within recommended range';

  if (nPerHa > 170) {
    status = 'excess';
    statusColor = '#ef4444'; // red
    message = 'Exceeds NVZ limit (170 kg N/ha)';
  } else if (nPerHa > 100) {
    status = 'high';
    statusColor = '#f59e0b'; // amber
    message = 'Approaching NVZ limit';
  } else if (nPerHa < 50) {
    status = 'low';
    statusColor = '#3b82f6'; // blue
    message = 'Below optimal range';
  }

  return {
    status,
    statusColor,
    message,
    nPerHa,
    margin: 170 - nPerHa // Margin to NVZ limit
  };
};

// Create default farm structure for new farms
export const createDefaultFarmStructure = () => {
  const kous = {};
  const pathways = [];

  // Create example fields
  const fields = [
    { id: 'field_1', name: 'Home Field', area: 15, use: FIELD_USE_TYPES.GRAZING_PLATFORM },
    { id: 'field_2', name: 'Middle Field', area: 18, use: FIELD_USE_TYPES.GRAZING_PLATFORM },
    { id: 'field_3', name: 'Far Field', area: 25, use: FIELD_USE_TYPES.SILAGE_GROUND },
  ];

  fields.forEach(field => {
    kous[field.id] = createKOU(KOU_TYPES.FIELD, field.id, field.name, {
      area: field.area,
      use: field.use,
      soilType: 'medium'
    });
  });

  // Create example livestock groups
  kous['herd_high'] = createKOU(KOU_TYPES.LIVESTOCK_GROUP, 'herd_high', 'High Yielders', {
    groupType: LIVESTOCK_GROUPS.MILKING_HIGH,
    animalCount: 60,
    avgWeight: 650,
    milkYield: 10000
  });

  kous['herd_mid'] = createKOU(KOU_TYPES.LIVESTOCK_GROUP, 'herd_mid', 'Mid Yielders', {
    groupType: LIVESTOCK_GROUPS.MILKING_MID,
    animalCount: 80,
    avgWeight: 650,
    milkYield: 8000
  });

  // Create feed stores
  kous['silage_clamp_1'] = createKOU(KOU_TYPES.FEED_STORE, 'silage_clamp_1', 'Grass Silage Clamp', {
    storeType: FEED_STORES.GRASS_SILAGE_CLAMP_1,
    capacity: 1500,
    currentStock: 1000,
    feedAnalysis: {
      DM: 30,
      CP: 14,
      N: 2.24,
      P: 0.35,
      K: 2.5
    }
  });

  kous['conc_store'] = createKOU(KOU_TYPES.FEED_STORE, 'conc_store', 'Concentrate Store', {
    storeType: FEED_STORES.CONCENTRATE_STORE,
    capacity: 200,
    currentStock: 100,
    feedAnalysis: {
      DM: 88,
      CP: 18,
      N: 2.88,
      P: 0.45,
      K: 0.8
    }
  });

  // Create manure stores
  kous['slurry_store_1'] = createKOU(KOU_TYPES.MANURE_STORE, 'slurry_store_1', 'Main Slurry Lagoon', {
    storeType: MANURE_STORES.SLURRY_LAGOON_1,
    capacity: 3000,
    currentStock: 1500,
    nutrientContent: {
      N: 3.0, // kg/m¬≥
      P: 0.6,
      K: 3.5,
      S: 0.3
    }
  });

  // Create outputs
  kous['milk_output'] = createKOU(KOU_TYPES.OUTPUT, 'milk_output', 'Milk Sales');
  kous['atmosphere'] = createKOU(KOU_TYPES.OUTPUT, 'atmosphere', 'Atmospheric Losses');

  // Create external inputs
  kous['fertilizer_supplier'] = createKOU(KOU_TYPES.EXTERNAL, 'fertilizer_supplier', 'Fertilizer Supplier');
  kous['feed_supplier'] = createKOU(KOU_TYPES.EXTERNAL, 'feed_supplier', 'Feed Supplier');

  return { kous, pathways };
};

// Export/Import functions
export const exportToJSON = (kous, pathways, metadata = {}) => {
  return JSON.stringify({
    version: '1.0',
    exported: new Date().toISOString(),
    metadata,
    kous,
    pathways
  }, null, 2);
};

export const importFromJSON = (jsonString) => {
  try {
    const data = JSON.parse(jsonString);
    if (!data.kous || !data.pathways) {
      throw new Error('Invalid data format');
    }
    return {
      kous: data.kous,
      pathways: data.pathways,
      metadata: data.metadata || {}
    };
  } catch (error) {
    throw new Error('Failed to import data: ' + error.message);
  }
};

// Export to CSV for spreadsheet users
export const exportPathwaysToCSV = (pathways, kous) => {
  const headers = ['From', 'To', 'Type', 'N (kg)', 'P (kg)', 'K (kg)', 'S (kg)'];
  const rows = pathways.map(p => [
    kous[p.from]?.name || p.from,
    kous[p.to]?.name || p.to,
    p.type.replace(/_/g, ' '),
    p.nutrients.N || 0,
    p.nutrients.P || 0,
    p.nutrients.K || 0,
    p.nutrients.S || 0
  ]);

  const csv = [headers, ...rows]
    .map(row => row.map(cell => `"${cell}"`).join(','))
    .join('\n');

  return csv;
};

