import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import { 
  AlertTriangle, CheckCircle, Download, Upload, Info, HelpCircle, 
  Plus, Trash2, Edit2, Save, X, ChevronRight, MapPin, Package, 
  Droplet, Leaf, DollarSign, FileText, Settings, Database, 
  TrendingUp, TrendingDown, Copy, Eye, EyeOff
} from 'lucide-react';
import {
  KOU_TYPES,
  FIELD_USE_TYPES,
  LIVESTOCK_GROUPS,
  PATHWAY_TYPES,
  createKOU,
  createPathway,
  calculateKOUBalance,
  calculateFarmBalance,
  getFieldStatus,
  calculateNutrientValue,
  createDefaultFarmStructure,
  exportToJSON,
  importFromJSON,
  exportPathwaysToCSV
} from '../data/kouStructure';

const FarmNutrientPlanner = () => {
  // Initialize state with saved data or defaults
  const [farmData, setFarmData] = useState(() => {
    const saved = localStorage.getItem('farmNutrientData');
    if (saved) {
      try {
        const parsed = JSON.parse(saved);
        return { kous: parsed.kous || {}, pathways: parsed.pathways || [] };
      } catch (e) {
        console.error('Error loading saved data:', e);
      }
    }
    return createDefaultFarmStructure();
  });

  const [activeView, setActiveView] = useState('overview');
  const [selectedKOU, setSelectedKOU] = useState(null);
  const [selectedNutrient, setSelectedNutrient] = useState('N');
  const [showDataManager, setShowDataManager] = useState(false);
  const [showHelp, setShowHelp] = useState(false);
  const [editingPathway, setEditingPathway] = useState(null);
  const [scenarios, setScenarios] = useState([]);
  const [activeScenario, setActiveScenario] = useState(null);

  // Auto-save to localStorage
  useEffect(() => {
    localStorage.setItem('farmNutrientData', JSON.stringify(farmData));
  }, [farmData]);

  // Calculate farm-wide metrics
  const farmBalance = calculateFarmBalance(farmData.kous, farmData.pathways);
  const totalArea = Object.values(farmData.kous)
    .filter(k => k.type === KOU_TYPES.FIELD)
    .reduce((sum, field) => sum + (field.properties.area || 0), 0);

  const nBalancePerHa = totalArea > 0 ? farmBalance.N.balance / totalArea : 0;
  const isNVZCompliant = nBalancePerHa <= 170;

  // Get KOU icon
  const getKOUIcon = (type) => {
    switch(type) {
      case KOU_TYPES.FIELD: return <MapPin className="w-5 h-5 text-green-600" />;
      case KOU_TYPES.LIVESTOCK_GROUP: return <span className="text-xl">üêÑ</span>;
      case KOU_TYPES.FEED_STORE: return <Package className="w-5 h-5 text-yellow-600" />;
      case KOU_TYPES.MANURE_STORE: return <Droplet className="w-5 h-5 text-amber-700" />;
      case KOU_TYPES.OUTPUT: return <TrendingUp className="w-5 h-5 text-blue-600" />;
      case KOU_TYPES.EXTERNAL: return <TrendingDown className="w-5 h-5 text-purple-600" />;
      default: return <Settings className="w-5 h-5 text-gray-600" />;
    }
  };

  // Quick Stats Component
  const QuickStats = () => (
    <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
      <div className="bg-white p-4 rounded-lg shadow">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm text-gray-600">N Balance</p>
            <p className="text-2xl font-bold text-gray-900">
              {farmBalance.N.balance.toFixed(0)} kg
            </p>
            <p className="text-sm text-gray-500">
              {nBalancePerHa.toFixed(1)} kg/ha
            </p>
          </div>
          <Droplet className="w-8 h-8 text-blue-500" />
        </div>
      </div>

      <div className="bg-white p-4 rounded-lg shadow">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm text-gray-600">P Balance</p>
            <p className="text-2xl font-bold text-gray-900">
              {farmBalance.P.balance.toFixed(0)} kg
            </p>
            <p className="text-sm text-gray-500">
              {totalArea > 0 ? (farmBalance.P.balance / totalArea).toFixed(1) : 0} kg/ha
            </p>
          </div>
          <Leaf className="w-8 h-8 text-green-500" />
        </div>
      </div>

      <div className="bg-white p-4 rounded-lg shadow">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm text-gray-600">N Efficiency</p>
            <p className="text-2xl font-bold text-gray-900">
              {farmBalance.N.efficiency.toFixed(1)}%
            </p>
            <p className="text-sm text-gray-500">
              {farmBalance.N.efficiency > 30 ? 'Good' : 'Can improve'}
            </p>
          </div>
          <TrendingUp className="w-8 h-8 text-purple-500" />
        </div>
      </div>

      <div className="bg-white p-4 rounded-lg shadow">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm text-gray-600">Nutrient Value</p>
            <p className="text-2xl font-bold text-gray-900">
              ¬£{calculateNutrientValue(farmBalance.N).toFixed(0)}
            </p>
            <p className="text-sm text-gray-500">
              In slurry stores
            </p>
          </div>
          <DollarSign className="w-8 h-8 text-green-600" />
        </div>
      </div>
    </div>
  );

  // Main Overview View
  const OverviewView = () => {
    const kousByType = Object.values(farmData.kous).reduce((acc, kou) => {
      if (!acc[kou.type]) acc[kou.type] = [];
      acc[kou.type].push(kou);
      return acc;
    }, {});

    return (
      <div className="space-y-6">
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">Farm System Overview</h2>

          <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-6">
            {Object.entries(kousByType).map(([type, kous]) => (
              <div key={type} className="space-y-3">
                <h3 className="font-medium text-gray-700 flex items-center gap-2">
                  {getKOUIcon(type)}
                  {type.replace(/_/g, ' ').toUpperCase()} ({kous.length})
                </h3>
                <div className="space-y-2">
                  {kous.map(kou => {
                    const balance = calculateKOUBalance(kou, farmData.pathways);
                    return (
                      <div
                        key={kou.id}
                        onClick={() => setSelectedKOU(kou)}
                        className="p-3 rounded-lg border-2 cursor-pointer transition-all hover:shadow-md bg-gray-50 hover:bg-gray-100"
                      >
                        <div className="flex justify-between items-center">
                          <div>
                            <div className="font-medium">{kou.name}</div>
                            <div className="text-xs text-gray-600">
                              {kou.type === KOU_TYPES.FIELD && `${kou.properties.area} ha`}
                              {kou.type === KOU_TYPES.LIVESTOCK_GROUP && `${kou.properties.animalCount} head`}
                              {kou.type === KOU_TYPES.FEED_STORE && `${kou.properties.currentStock}/${kou.properties.capacity} t`}
                              {kou.type === KOU_TYPES.MANURE_STORE && `${kou.properties.currentStock}/${kou.properties.capacity} m¬≥`}
                            </div>
                          </div>
                          <div className="text-right">
                            <div className="text-xs text-gray-500">N Balance</div>
                            <div className={`text-sm font-medium ${balance.N.balance >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                              {balance.N.balance >= 0 ? '+' : ''}{balance.N.balance.toFixed(0)} kg
                            </div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Nutrient Flow Summary Chart */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-bold text-gray-900 mb-4">Nutrient Balance Overview</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={[
              { name: 'Nitrogen', inputs: farmBalance.N.inputs, outputs: farmBalance.N.outputs, balance: farmBalance.N.balance },
              { name: 'Phosphorus', inputs: farmBalance.P.inputs, outputs: farmBalance.P.outputs, balance: farmBalance.P.balance },
              { name: 'Potassium', inputs: farmBalance.K.inputs, outputs: farmBalance.K.outputs, balance: farmBalance.K.balance }
            ]}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="inputs" fill="#3b82f6" name="Inputs (kg)" />
              <Bar dataKey="outputs" fill="#10b981" name="Outputs (kg)" />
              <Bar dataKey="balance" fill="#f59e0b" name="Balance (kg)" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>
    );
  };

  // Field Map View
  const FieldMapView = () => {
    const fields = Object.values(farmData.kous).filter(k => k.type === KOU_TYPES.FIELD);

    return (
      <div className="bg-white rounded-lg shadow-lg p-6">
        <h3 className="text-lg font-bold text-gray-900 mb-4">Field Nutrient Status Map</h3>

        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {fields.map(field => {
            const status = getFieldStatus(field, farmData.pathways);
            const balance = calculateKOUBalance(field, farmData.pathways);

            return (
              <div
                key={field.id}
                onClick={() => setSelectedKOU(field)}
                className="relative p-4 rounded-lg border-2 cursor-pointer transition-all hover:shadow-lg"
                style={{ borderColor: status.statusColor, backgroundColor: status.statusColor + '20' }}
              >
                <div className="flex justify-between items-start mb-2">
                  <h4 className="font-bold text-gray-900">{field.name}</h4>
                  <MapPin className="w-5 h-5" style={{ color: status.statusColor }} />
                </div>

                <div className="space-y-1 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Area:</span>
                    <span className="font-medium">{field.properties.area} ha</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">N Applied:</span>
                    <span className="font-medium">{balance.N.inputs.toFixed(0)} kg</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">N/ha:</span>
                    <span className="font-bold" style={{ color: status.statusColor }}>
                      {status.nPerHa.toFixed(1)} kg/ha
                    </span>
                  </div>
                </div>

                <div className="mt-3 pt-3 border-t border-gray-200">
                  <div className="text-xs text-gray-600">{status.message}</div>
                  {status.margin < 20 && status.margin > 0 && (
                    <div className="text-xs font-medium text-amber-600 mt-1">
                      ‚ö†Ô∏è Within 20kg of limit
                    </div>
                  )}
                </div>
              </div>
            );
          })}
        </div>

        <div className="mt-6 p-4 bg-gray-50 rounded-lg">
          <h4 className="font-medium text-gray-700 mb-2">Legend</h4>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-2 text-sm">
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 rounded" style={{ backgroundColor: '#ef4444' }}></div>
              <span>Excess (&gt;170 kg N/ha)</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 rounded" style={{ backgroundColor: '#f59e0b' }}></div>
              <span>High (100-170)</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 rounded" style={{ backgroundColor: '#10b981' }}></div>
              <span>Optimal (50-100)</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 rounded" style={{ backgroundColor: '#3b82f6' }}></div>
              <span>Low (&lt;50)</span>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Pathway Management View
  const PathwaysView = () => {
    const [showAddPathway, setShowAddPathway] = useState(false);
    const [newPathway, setNewPathway] = useState({
      from: '',
      to: '',
      type: PATHWAY_TYPES.FEEDING,
      nutrients: { N: 0, P: 0, K: 0, S: 0 }
    });

    const handleAddPathway = () => {
      if (newPathway.from && newPathway.to) {
        const pathway = createPathway(
          newPathway.from,
          newPathway.to,
          newPathway.type,
          newPathway.nutrients
        );
        setFarmData(prev => ({
          ...prev,
          pathways: [...prev.pathways, pathway]
        }));
        setShowAddPathway(false);
        setNewPathway({
          from: '',
          to: '',
          type: PATHWAY_TYPES.FEEDING,
          nutrients: { N: 0, P: 0, K: 0, S: 0 }
        });
      }
    };

    const handleDeletePathway = (pathwayId) => {
      setFarmData(prev => ({
        ...prev,
        pathways: prev.pathways.filter(p => p.id !== pathwayId)
      }));
    };

    return (
      <div className="space-y-6">
        <div className="bg-white rounded-lg shadow-lg p-6">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-lg font-bold text-gray-900">Nutrient Pathways</h3>
            <button
              onClick={() => setShowAddPathway(true)}
              className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >
              <Plus className="w-4 h-4" />
              Add Pathway
            </button>
          </div>

          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">From</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">To</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Type</th>
                  <th className="px-4 py-3 text-right text-xs font-medium text-gray-500 uppercase">N (kg)</th>
                  <th className="px-4 py-3 text-right text-xs font-medium text-gray-500 uppercase">P (kg)</th>
                  <th className="px-4 py-3 text-right text-xs font-medium text-gray-500 uppercase">K (kg)</th>
                  <th className="px-4 py-3 text-right text-xs font-medium text-gray-500 uppercase">Actions</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {farmData.pathways.map(pathway => (
                  <tr key={pathway.id} className="hover:bg-gray-50">
                    <td className="px-4 py-3 text-sm">
                      <div className="flex items-center gap-2">
                        {getKOUIcon(farmData.kous[pathway.from]?.type)}
                        {farmData.kous[pathway.from]?.name || pathway.from}
                      </div>
                    </td>
                    <td className="px-4 py-3 text-sm">
                      <div className="flex items-center gap-2">
                        {getKOUIcon(farmData.kous[pathway.to]?.type)}
                        {farmData.kous[pathway.to]?.name || pathway.to}
                      </div>
                    </td>
                    <td className="px-4 py-3 text-sm capitalize">
                      {pathway.type.replace(/_/g, ' ')}
                    </td>
                    <td className="px-4 py-3 text-sm text-right font-medium">{pathway.nutrients.N}</td>
                    <td className="px-4 py-3 text-sm text-right font-medium">{pathway.nutrients.P}</td>
                    <td className="px-4 py-3 text-sm text-right font-medium">{pathway.nutrients.K}</td>
                    <td className="px-4 py-3 text-sm text-right">
                      <button
                        onClick={() => handleDeletePathway(pathway.id)}
                        className="text-red-600 hover:text-red-800"
                      >
                        <Trash2 className="w-4 h-4" />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Add Pathway Modal */}
        {showAddPathway && (
          <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
            <div className="bg-white rounded-lg shadow-xl max-w-md w-full p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">Add Nutrient Pathway</h3>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">From</label>
                  <select
                    value={newPathway.from}
                    onChange={(e) => setNewPathway(prev => ({ ...prev, from: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                  >
                    <option value="">Select source...</option>
                    {Object.values(farmData.kous).map(kou => (
                      <option key={kou.id} value={kou.id}>
                        {kou.name} ({kou.type.replace(/_/g, ' ')})
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">To</label>
                  <select
                    value={newPathway.to}
                    onChange={(e) => setNewPathway(prev => ({ ...prev, to: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                  >
                    <option value="">Select destination...</option>
                    {Object.values(farmData.kous).map(kou => (
                      <option key={kou.id} value={kou.id}>
                        {kou.name} ({kou.type.replace(/_/g, ' ')})
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Type</label>
                  <select
                    value={newPathway.type}
                    onChange={(e) => setNewPathway(prev => ({ ...prev, type: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                  >
                    {Object.entries(PATHWAY_TYPES).map(([key, value]) => (
                      <option key={key} value={value}>
                        {value.replace(/_/g, ' ').toUpperCase()}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="grid grid-cols-2 gap-3">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">N (kg/year)</label>
                    <input
                      type="number"
                      value={newPathway.nutrients.N}
                      onChange={(e) => setNewPathway(prev => ({
                        ...prev,
                        nutrients: { ...prev.nutrients, N: parseFloat(e.target.value) || 0 }
                      }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">P (kg/year)</label>
                    <input
                      type="number"
                      value={newPathway.nutrients.P}
                      onChange={(e) => setNewPathway(prev => ({
                        ...prev,
                        nutrients: { ...prev.nutrients, P: parseFloat(e.target.value) || 0 }
                      }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">K (kg/year)</label>
                    <input
                      type="number"
                      value={newPathway.nutrients.K}
                      onChange={(e) => setNewPathway(prev => ({
                        ...prev,
                        nutrients: { ...prev.nutrients, K: parseFloat(e.target.value) || 0 }
                      }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">S (kg/year)</label>
                    <input
                      type="number"
                      value={newPathway.nutrients.S}
                      onChange={(e) => setNewPathway(prev => ({
                        ...prev,
                        nutrients: { ...prev.nutrients, S: parseFloat(e.target.value) || 0 }
                      }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                    />
                  </div>
                </div>
              </div>

              <div className="flex justify-end gap-3 mt-6">
                <button
                  onClick={() => setShowAddPathway(false)}
                  className="px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50"
                >
                  Cancel
                </button>
                <button
                  onClick={handleAddPathway}
                  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                >
                  Add Pathway
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    );
  };

  // Help Modal
  const HelpModal = () => (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-lg shadow-xl max-w-4xl w-full max-h-[90vh] overflow-y-auto p-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-bold text-gray-900">Consultant Quick Guide</h2>
          <button
            onClick={() => setShowHelp(false)}
            className="text-gray-500 hover:text-gray-700"
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        <div className="space-y-6">
          <section>
            <h3 className="text-lg font-bold text-gray-800 mb-2">üöÄ Quick Start (5 minutes)</h3>
            <ol className="list-decimal list-inside space-y-2 text-gray-700">
              <li>Click "Data Management" to set up farm fields and livestock groups</li>
              <li>Add main nutrient flows using "Pathways" tab</li>
              <li>Check NVZ compliance in the header alert</li>
              <li>Review field status in "Field Map" view</li>
              <li>Test scenarios for compliance options</li>
            </ol>
          </section>

          <section>
            <h3 className="text-lg font-bold text-gray-800 mb-2">üìä Key Metrics Explained</h3>
            <div className="bg-gray-50 rounded-lg p-4 space-y-2 text-sm">
              <div><strong>N Balance:</strong> Total N inputs minus outputs (aim for &lt;170 kg/ha)</div>
              <div><strong>N Efficiency:</strong> Productive outputs √∑ inputs (target &gt;30%)</div>
              <div><strong>Field Status Colors:</strong></div>
              <ul className="list-disc list-inside ml-4">
                <li>üî¥ Red = Over 170 kg N/ha (NVZ breach)</li>
                <li>üü° Amber = 100-170 kg N/ha (approaching limit)</li>
                <li>üü¢ Green = 50-100 kg N/ha (optimal)</li>
                <li>üîµ Blue = Under 50 kg N/ha (may need more)</li>
              </ul>
            </div>
          </section>

          <section>
            <h3 className="text-lg font-bold text-gray-800 mb-2">üí° Time-Saving Tips</h3>
            <ul className="list-disc list-inside space-y-1 text-gray-700">
              <li>Start with major flows only - refine later</li>
              <li>Use typical values if exact data unavailable:
                <ul className="list-disc list-inside ml-6 text-sm">
                  <li>Cattle slurry: 3 kg N/m¬≥</li>
                  <li>FYM: 6 kg N/tonne</li>
                  <li>Grass silage: 4 kg N/tonne fresh</li>
                </ul>
              </li>
              <li>Export data regularly to save progress</li>
              <li>Group similar fields if many small paddocks</li>
            </ul>
          </section>

          <section>
            <h3 className="text-lg font-bold text-gray-800 mb-2">üéØ Common Scenarios</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="border rounded-lg p-3">
                <h4 className="font-medium text-gray-800 mb-1">NVZ Compliance Issues</h4>
                <ul className="text-sm text-gray-600 space-y-1">
                  <li>‚Ä¢ Reduce slurry to high-risk fields</li>
                  <li>‚Ä¢ Export slurry to neighbors</li>
                  <li>‚Ä¢ Increase crop yields/offtake</li>
                </ul>
              </div>
              <div className="border rounded-lg p-3">
                <h4 className="font-medium text-gray-800 mb-1">Cost Reduction</h4>
                <ul className="text-sm text-gray-600 space-y-1">
                  <li>‚Ä¢ Cut fertilizer where manure N is high</li>
                  <li>‚Ä¢ Optimize feed efficiency</li>
                  <li>‚Ä¢ Better utilize home-grown feeds</li>
                </ul>
              </div>
            </div>
          </section>
        </div>

        <div className="mt-6 pt-6 border-t border-gray-200">
          <button
            onClick={() => setShowHelp(false)}
            className="w-full px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Close Help
          </button>
        </div>
      </div>
    </div>
  );

  // Data Management Modal
  const DataManager = () => {
    const [activeTab, setActiveTab] = useState('fields');
    const [editingKOU, setEditingKOU] = useState(null);

    const handleImport = (event) => {
      const file = event.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = (e) => {
          try {
            const imported = importFromJSON(e.target.result);
            setFarmData({ kous: imported.kous, pathways: imported.pathways });
            setShowDataManager(false);
          } catch (error) {
            alert('Error importing file: ' + error.message);
          }
        };
        reader.readAsText(file);
      }
    };

    const handleExport = () => {
      const dataStr = exportToJSON(farmData.kous, farmData.pathways, {
        farmName: 'Farm Export',
        exportDate: new Date().toISOString()
      });
      const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);

      const linkElement = document.createElement('a');
      linkElement.setAttribute('href', dataUri);
      linkElement.setAttribute('download', `farm-nutrient-data-${new Date().toISOString().split('T')[0]}.json`);
      linkElement.click();
    };

    const handleExportCSV = () => {
      const csv = exportPathwaysToCSV(farmData.pathways, farmData.kous);
      const dataUri = 'data:text/csv;charset=utf-8,'+ encodeURIComponent(csv);

      const linkElement = document.createElement('a');
      linkElement.setAttribute('href', dataUri);
      linkElement.setAttribute('download', `nutrient-pathways-${new Date().toISOString().split('T')[0]}.csv`);
      linkElement.click();
    };

    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
        <div className="bg-white rounded-lg shadow-xl max-w-6xl w-full max-h-[90vh] overflow-hidden">
          <div className="p-6 border-b border-gray-200">
            <div className="flex justify-between items-center">
              <h2 className="text-2xl font-bold text-gray-900">Data Management</h2>
              <div className="flex items-center gap-3">
                <label className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 cursor-pointer flex items-center gap-2">
                  <Upload className="w-4 h-4" />
                  Import JSON
                  <input type="file" onChange={handleImport} className="hidden" accept=".json" />
                </label>
                <button
                  onClick={handleExport}
                  className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 flex items-center gap-2"
                >
                  <Download className="w-4 h-4" />
                  Export JSON
                </button>
                <button
                  onClick={handleExportCSV}
                  className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 flex items-center gap-2"
                >
                  <FileText className="w-4 h-4" />
                  Export CSV
                </button>
                <button
                  onClick={() => setShowDataManager(false)}
                  className="text-gray-500 hover:text-gray-700"
                >
                  <X className="w-6 h-6" />
                </button>
              </div>
            </div>
          </div>

          <div className="flex h-[calc(90vh-120px)]">
            {/* Sidebar */}
            <div className="w-48 bg-gray-50 p-4 border-r border-gray-200">
              <nav className="space-y-2">
                {['fields', 'livestock', 'stores', 'external'].map(tab => (
                  <button
                    key={tab}
                    onClick={() => setActiveTab(tab)}
                    className={`w-full text-left px-3 py-2 rounded-lg capitalize ${
                      activeTab === tab
                        ? 'bg-blue-600 text-white'
                        : 'text-gray-700 hover:bg-gray-200'
                    }`}
                  >
                    {tab}
                  </button>
                ))}
              </nav>
            </div>

            {/* Content */}
            <div className="flex-1 p-6 overflow-y-auto">
              {activeTab === 'fields' && (
                <div>
                  <div className="flex justify-between items-center mb-4">
                    <h3 className="text-lg font-bold text-gray-900">Fields</h3>
                    <button
                      onClick={() => {
                        const newId = `field_${Date.now()}`;
                        const newField = createKOU(KOU_TYPES.FIELD, newId, 'New Field', {
                          area: 10,
                          use: FIELD_USE_TYPES.GRAZING_PLATFORM
                        });
                        setFarmData(prev => ({
                          ...prev,
                          kous: { ...prev.kous, [newId]: newField }
                        }));
                      }}
                      className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 flex items-center gap-2"
                    >
                      <Plus className="w-4 h-4" />
                      Add Field
                    </button>
                  </div>

                  <div className="space-y-3">
                    {Object.values(farmData.kous)
                      .filter(k => k.type === KOU_TYPES.FIELD)
                      .map(field => (
                        <div key={field.id} className="bg-white border rounded-lg p-4">
                          <div className="flex justify-between items-center">
                            <div className="flex-1 grid grid-cols-3 gap-4">
                              <input
                                type="text"
                                value={field.name}
                                onChange={(e) => {
                                  setFarmData(prev => ({
                                    ...prev,
                                    kous: {
                                      ...prev.kous,
                                      [field.id]: { ...field, name: e.target.value }
                                    }
                                  }));
                                }}
                                className="px-3 py-2 border border-gray-300 rounded-lg"
                                placeholder="Field name"
                              />
                              <input
                                type="number"
                                value={field.properties.area}
                                onChange={(e) => {
                                  setFarmData(prev => ({
                                    ...prev,
                                    kous: {
                                      ...prev.kous,
                                      [field.id]: {
                                        ...field,
                                        properties: { ...field.properties, area: parseFloat(e.target.value) || 0 }
                                      }
                                    }
                                  }));
                                }}
                                className="px-3 py-2 border border-gray-300 rounded-lg"
                                placeholder="Area (ha)"
                              />
                              <select
                                value={field.properties.use}
                                onChange={(e) => {
                                  setFarmData(prev => ({
                                    ...prev,
                                    kous: {
                                      ...prev.kous,
                                      [field.id]: {
                                        ...field,
                                        properties: { ...field.properties, use: e.target.value }
                                      }
                                    }
                                  }));
                                }}
                                className="px-3 py-2 border border-gray-300 rounded-lg"
                              >
                                {Object.entries(FIELD_USE_TYPES).map(([key, value]) => (
                                  <option key={key} value={value}>
                                    {value.replace(/_/g, ' ').toUpperCase()}
                                  </option>
                                ))}
                              </select>
                            </div>
                            <button
                              onClick={() => {
                                setFarmData(prev => {
                                  const newKous = { ...prev.kous };
                                  delete newKous[field.id];
                                  return {
                                    ...prev,
                                    kous: newKous,
                                    pathways: prev.pathways.filter(p => p.from !== field.id && p.to !== field.id)
                                  };
                                });
                              }}
                              className="ml-4 text-red-600 hover:text-red-800"
                            >
                              <Trash2 className="w-5 h-5" />
                            </button>
                          </div>
                        </div>
                      ))}
                  </div>
                </div>
              )}

              {/* Similar sections for livestock, stores, and external */}
              {activeTab === 'livestock' && (
                <div>
                  <h3 className="text-lg font-bold text-gray-900 mb-4">Livestock Groups</h3>
                  <p className="text-gray-600">Manage livestock groups here...</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Farm Nutrient Planner</h1>
              <p className="text-sm text-gray-600">Consultant-Ready Nutrient Budget Tool</p>
            </div>
            <div className="flex items-center gap-4">
              <button
                onClick={() => setShowHelp(true)}
                className="p-2 rounded-full hover:bg-gray-100 text-gray-500"
              >
                <HelpCircle className="w-6 h-6" />
              </button>
              <button
                onClick={() => setShowDataManager(true)}
                className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 flex items-center gap-2"
              >
                <Database className="w-4 h-4" />
                Data Management
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* NVZ Compliance Alert */}
      <div className={`px-4 sm:px-6 lg:px-8 py-3 ${
        isNVZCompliant ? 'bg-green-100' : 'bg-red-100'
      }`}>
        <div className="max-w-7xl mx-auto flex items-center gap-3">
          {isNVZCompliant ? (
            <CheckCircle className="w-6 h-6 text-green-700" />
          ) : (
            <AlertTriangle className="w-6 h-6 text-red-700" />
          )}
          <div className="flex-1">
            <p className={`font-semibold ${isNVZCompliant ? 'text-green-800' : 'text-red-800'}`}>
              NVZ Compliance: {isNVZCompliant ? 'COMPLIANT' : 'NON-COMPLIANT'}
            </p>
            <p className={`text-sm ${isNVZCompliant ? 'text-green-700' : 'text-red-700'}`}>
              Current N balance: {nBalancePerHa.toFixed(1)} kg/ha (Limit: 170 kg/ha, Margin: {(170 - nBalancePerHa).toFixed(1)} kg/ha)
            </p>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Quick Stats */}
        <QuickStats />

        {/* Navigation Tabs */}
        <div className="bg-white rounded-lg shadow-sm mb-6">
          <nav className="flex border-b border-gray-200">
            {[
              { id: 'overview', label: 'Overview', icon: Eye },
              { id: 'fieldmap', label: 'Field Map', icon: MapPin },
              { id: 'pathways', label: 'Pathways', icon: ChevronRight },
            ].map(tab => (
              <button
                key={tab.id}
                onClick={() => setActiveView(tab.id)}
                className={`flex items-center gap-2 px-6 py-3 border-b-2 font-medium text-sm ${
                  activeView === tab.id
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <tab.icon className="w-4 h-4" />
                {tab.label}
              </button>
            ))}
          </nav>
        </div>

        {/* View Content */}
        {activeView === 'overview' && <OverviewView />}
        {activeView === 'fieldmap' && <FieldMapView />}
        {activeView === 'pathways' && <PathwaysView />}

        {/* KOU Detail View (when selected) */}
        {selectedKOU && (
          <div className="fixed inset-0 bg-black bg-opacity-50 z-40 flex items-center justify-center p-4">
            <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full p-6">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-bold text-gray-900">{selectedKOU.name}</h2>
                <button
                  onClick={() => setSelectedKOU(null)}
                  className="text-gray-500 hover:text-gray-700"
                >
                  <X className="w-6 h-6" />
                </button>
              </div>

              <div className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div className="bg-gray-50 rounded-lg p-4">
                    <p className="text-sm text-gray-600">Type</p>
                    <p className="font-medium capitalize">{selectedKOU.type.replace(/_/g, ' ')}</p>
                  </div>
                  {selectedKOU.type === KOU_TYPES.FIELD && (
                    <div className="bg-gray-50 rounded-lg p-4">
                      <p className="text-sm text-gray-600">Area</p>
                      <p className="font-medium">{selectedKOU.properties.area} ha</p>
                    </div>
                  )}
                </div>

                <div>
                  <h3 className="font-medium text-gray-900 mb-2">Nutrient Balance</h3>
                  <div className="grid grid-cols-4 gap-3">
                    {(() => {
                      const balance = calculateKOUBalance(selectedKOU, farmData.pathways);
                      return Object.entries(balance).map(([nutrient, data]) => (
                        <div key={nutrient} className="bg-gray-50 rounded-lg p-3">
                          <p className="text-sm font-medium text-gray-700">{nutrient}</p>
                          <p className={`text-lg font-bold ${data.balance >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                            {data.balance >= 0 ? '+' : ''}{data.balance.toFixed(0)} kg
                          </p>
                          <p className="text-xs text-gray-500">
                            In: {data.inputs.toFixed(0)} | Out: {data.outputs.toFixed(0)}
                          </p>
                        </div>
                      ));
                    })()}
                  </div>
                </div>

                {selectedKOU.type === KOU_TYPES.FIELD && (() => {
                  const status = getFieldStatus(selectedKOU, farmData.pathways);
                  return (
                    <div className={`p-4 rounded-lg border-2`} style={{ borderColor: status.statusColor, backgroundColor: status.statusColor + '20' }}>
                      <p className="font-medium" style={{ color: status.statusColor }}>
                        {status.status.toUpperCase()} - {status.nPerHa.toFixed(1)} kg N/ha
                      </p>
                      <p className="text-sm text-gray-700 mt-1">{status.message}</p>
                      <p className="text-sm text-gray-600 mt-1">
                        Margin to NVZ limit: {status.margin.toFixed(1)} kg/ha
                      </p>
                    </div>
                  );
                })()}
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Modals */}
      {showHelp && <HelpModal />}
      {showDataManager && <DataManager />}
    </div>
  );
};

export default FarmNutrientPlanner;
