input.content:
import React, { useState, useMemo, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area, RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, Radar, Treemap, Sankey } from 'recharts';
import { Calendar, TrendingUp, AlertCircle, Leaf, Globe, Package, Activity, Factory, Users, Truck, Info, ChevronDown, ChevronUp, Eye, Database, Building2 } from 'lucide-react';

const Dashboard = () => {
  const [selectedYear, setSelectedYear] = useState('all');
  const [selectedCustomer, setSelectedCustomer] = useState('all');
  const [selectedDataSource, setSelectedDataSource] = useState('all');
  const [expandedSections, setExpandedSections] = useState({
    feedCompanies: false,
    customerBreakdown: true
  });
  const [hoveredMetric, setHoveredMetric] = useState(null);

  // Enhanced mock data
  const feedData = {
    2: {
      feedCompany: 12500,
      farmerDeclared: 8300,
      total: 20800
    },
    3: {
      feedCompany: 13200,
      farmerDeclared: 8900,
      total: 22100
    }
  };

  const soyaBreakdown = {
    2: {
      soyBean: { tonnage: 3120, percentage: 15 },
      soyHull: { tonnage: 832, percentage: 4 },
      soyOil: { tonnage: 416, percentage: 2 }
    },
    3: {
      soyBean: { tonnage: 3315, percentage: 15 },
      soyHull: { tonnage: 884, percentage: 4 },
      soyOil: { tonnage: 442, percentage: 2 }
    }
  };

  const customerData = [
    { name: 'Tesco', percentage: 35, year2Volume: 7280, year3Volume: 7735 },
    { name: 'Sainsburys', percentage: 25, year2Volume: 5200, year3Volume: 5525 },
    { name: 'Waitrose', percentage: 20, year2Volume: 4160, year3Volume: 4420 },
    { name: 'Co-op', percentage: 12, year2Volume: 2496, year3Volume: 2652 },
    { name: 'Local Markets', percentage: 8, year2Volume: 1664, year3Volume: 1768 }
  ];

  const feedCompanies = [
    { name: 'Mole Valley Farmers', mills: ['Tiverton Mill', 'Cullompton Mill'], tonnage: 4500, producers: 45 },
    { name: 'ForFarmers UK', mills: ['Bury St Edmunds', 'Exeter Mill'], tonnage: 3800, producers: 38 },
    { name: 'Wynnstay Group', mills: ['Llansantffraid', 'Ashford Mill'], tonnage: 3200, producers: 32 },
    { name: 'NWF Agriculture', mills: ['Wardle Mill', 'Longtown Mill'], tonnage: 2800, producers: 28 },
    { name: 'Harpers Feeds', mills: ['Market Drayton'], tonnage: 2100, producers: 21 },
    { name: 'BOCM Pauls', mills: ['Burston Mill', 'Masham Mill'], tonnage: 1900, producers: 19 },
    { name: 'Local Feed Merchants', mills: ['Various'], tonnage: 1500, producers: 15 }
  ];

  // Calculate customer-specific soya usage
  const customerSoyaUsage = useMemo(() => {
    if (selectedCustomer === 'all') return null;

    const customer = customerData.find(c => c.name === selectedCustomer);
    if (!customer) return null;

    const year2Total = Object.values(soyaBreakdown[2]).reduce((sum, item) => sum + item.tonnage, 0);
    const year3Total = Object.values(soyaBreakdown[3]).reduce((sum, item) => sum + item.tonnage, 0);

    return {
      name: customer.name,
      percentage: customer.percentage,
      year2: {
        totalSoya: (year2Total * customer.percentage / 100).toFixed(0),
        soyBean: (soyaBreakdown[2].soyBean.tonnage * customer.percentage / 100).toFixed(0),
        soyHull: (soyaBreakdown[2].soyHull.tonnage * customer.percentage / 100).toFixed(0),
        soyOil: (soyaBreakdown[2].soyOil.tonnage * customer.percentage / 100).toFixed(0)
      },
      year3: {
        totalSoya: (year3Total * customer.percentage / 100).toFixed(0),
        soyBean: (soyaBreakdown[3].soyBean.tonnage * customer.percentage / 100).toFixed(0),
        soyHull: (soyaBreakdown[3].soyHull.tonnage * customer.percentage / 100).toFixed(0),
        soyOil: (soyaBreakdown[3].soyOil.tonnage * customer.percentage / 100).toFixed(0)
      }
    };
  }, [selectedCustomer]);

  // Treemap data for soya breakdown
  const soyaTreemapData = useMemo(() => {
    const year = selectedYear === 'all' ? '3' : selectedYear;
    if (year === 'all') return [];

    return [
      {
        name: 'Soy Products',
        children: [
          { name: 'Soy Bean', size: soyaBreakdown[year].soyBean.tonnage, percentage: soyaBreakdown[year].soyBean.percentage },
          { name: 'Soy Hull', size: soyaBreakdown[year].soyHull.tonnage, percentage: soyaBreakdown[year].soyHull.percentage },
          { name: 'Soy Oil', size: soyaBreakdown[year].soyOil.tonnage, percentage: soyaBreakdown[year].soyOil.percentage }
        ]
      }
    ];
  }, [selectedYear]);

  // Animated counter component
  const AnimatedNumber = ({ value, suffix = '' }) => {
    const [displayValue, setDisplayValue] = useState(0);

    useEffect(() => {
      const duration = 1000;
      const steps = 50;
      const increment = value / steps;
      let current = 0;

      const timer = setInterval(() => {
        current += increment;
        if (current >= value) {
          setDisplayValue(value);
          clearInterval(timer);
        } else {
          setDisplayValue(Math.floor(current));
        }
      }, duration / steps);

      return () => clearInterval(timer);
    }, [value]);

    return <span>{displayValue.toLocaleString()}{suffix}</span>;
  };

  // Custom tooltip
  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-gray-900 text-white p-3 rounded-lg shadow-xl border border-gray-700">
          <p className="font-semibold mb-1">{label}</p>
          {payload.map((entry, index) => (
            <p key={index} className="text-sm">
              <span style={{ color: entry.color }}>{entry.name}:</span> {entry.value.toLocaleString()}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  const COLORS = {
    primary: '#6366f1',
    secondary: '#8b5cf6',
    success: '#10b981',
    warning: '#f59e0b',
    danger: '#ef4444',
    info: '#06b6d4',
    soyBean: '#f59e0b',
    soyHull: '#fb923c',
    soyOil: '#fbbf24'
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Animated Header */}
        <div className="mb-8">
          <div className="flex items-center gap-4 mb-2">
            <div className="p-3 bg-gradient-to-br from-indigo-500 to-purple-600 rounded-lg shadow-lg">
              <Leaf className="h-8 w-8 text-white" />
            </div>
            <div>
              <h1 className="text-4xl font-bold text-white mb-1 bg-gradient-to-r from-indigo-400 to-purple-400 bg-clip-text text-transparent">
                Feed & Emissions Intelligence Platform
              </h1>
              <p className="text-gray-400">Producer Group â€¢ 3KEEL Reporting System</p>
            </div>
          </div>
        </div>

        {/* Enhanced Filters */}
        <div className="bg-gray-800/50 backdrop-blur-lg rounded-xl shadow-xl p-5 mb-6 border border-gray-700">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">Year</label>
              <select 
                value={selectedYear} 
                onChange={(e) => setSelectedYear(e.target.value)}
                className="w-full px-4 py-2 bg-gray-700 border border-gray-600 text-white rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-all"
              >
                <option value="all">All Years</option>
                <option value="2">Year 2</option>
                <option value="3">Year 3</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">Customer View</label>
              <select 
                value={selectedCustomer} 
                onChange={(e) => setSelectedCustomer(e.target.value)}
                className="w-full px-4 py-2 bg-gray-700 border border-gray-600 text-white rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-all"
              >
                <option value="all">All Customers</option>
                {customerData.map(c => (
                  <option key={c.name} value={c.name}>{c.name}</option>
                ))}
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">Data Source</label>
              <select 
                value={selectedDataSource} 
                onChange={(e) => setSelectedDataSource(e.target.value)}
                className="w-full px-4 py-2 bg-gray-700 border border-gray-600 text-white rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-all"
              >
                <option value="all">All Sources</option>
                <option value="feedCompany">Feed Company Data</option>
                <option value="farmerDeclared">Farmer Declared</option>
              </select>
            </div>
            <div className="flex items-end">
              <button className="w-full px-4 py-2 bg-gradient-to-r from-indigo-500 to-purple-600 text-white rounded-lg hover:from-indigo-600 hover:to-purple-700 transition-all shadow-lg flex items-center justify-center gap-2">
                <Eye className="h-4 w-4" />
                Export Report
              </button>
            </div>
          </div>
        </div>

        {/* Main Summary Cards with Animations */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
          <div 
            className="bg-gradient-to-br from-gray-800 to-gray-700 rounded-xl shadow-xl p-6 border border-gray-600 hover:scale-105 transition-transform cursor-pointer"
            onMouseEnter={() => setHoveredMetric('totalFeed')}
            onMouseLeave={() => setHoveredMetric(null)}
          >
            <div className="flex items-center justify-between mb-3">
              <h3 className="text-sm font-medium text-gray-300">Total Feed (All Types)</h3>
              <Package className="h-6 w-6 text-indigo-400" />
            </div>
            <p className="text-3xl font-bold text-white mb-2">
              <AnimatedNumber value={selectedYear === 'all' ? 42900 : feedData[selectedYear]?.total || 0} suffix=" MT" />
            </p>
            <div className="space-y-1">
              <div className="flex justify-between text-xs">
                <span className="text-gray-400">Feed Company</span>
                <span className="text-indigo-400 font-semibold">
                  {selectedYear === 'all' ? '25,700' : feedData[selectedYear]?.feedCompany.toLocaleString()} MT
                </span>
              </div>
              <div className="flex justify-between text-xs">
                <span className="text-gray-400">Farmer Declared</span>
                <span className="text-purple-400 font-semibold">
                  {selectedYear === 'all' ? '17,200' : feedData[selectedYear]?.farmerDeclared.toLocaleString()} MT
                </span>
              </div>
            </div>
          </div>

          <div 
            className="bg-gradient-to-br from-yellow-900 to-orange-900 rounded-xl shadow-xl p-6 border border-yellow-700 hover:scale-105 transition-transform cursor-pointer"
            onMouseEnter={() => setHoveredMetric('soyaTotal')}
            onMouseLeave={() => setHoveredMetric(null)}
          >
            <div className="flex items-center justify-between mb-3">
              <h3 className="text-sm font-medium text-yellow-200">Total Soya Content</h3>
              <Leaf className="h-6 w-6 text-yellow-400" />
            </div>
            <p className="text-3xl font-bold text-white mb-2">
              <AnimatedNumber 
                value={selectedYear === 'all' ? 8709 : Object.values(soyaBreakdown[selectedYear] || {}).reduce((sum, item) => sum + item.tonnage, 0)} 
                suffix=" MT" 
              />
            </p>
            <div className="space-y-1">
              <div className="flex justify-between text-xs">
                <span className="text-yellow-300">Soy Bean</span>
                <span className="text-yellow-400 font-semibold">
                  {selectedYear === 'all' ? '6,435' : soyaBreakdown[selectedYear]?.soyBean.tonnage.toLocaleString()} MT
                </span>
              </div>
              <div className="flex justify-between text-xs">
                <span className="text-yellow-300">Soy Hull</span>
                <span className="text-orange-400 font-semibold">
                  {selectedYear === 'all' ? '1,716' : soyaBreakdown[selectedYear]?.soyHull.tonnage.toLocaleString()} MT
                </span>
              </div>
              <div className="flex justify-between text-xs">
                <span className="text-yellow-300">Soy Oil</span>
                <span className="text-orange-300 font-semibold">
                  {selectedYear === 'all' ? '858' : soyaBreakdown[selectedYear]?.soyOil.tonnage.toLocaleString()} MT
                </span>
              </div>
            </div>
          </div>

          <div className="bg-gradient-to-br from-green-900 to-emerald-900 rounded-xl shadow-xl p-6 border border-green-700 hover:scale-105 transition-transform">
            <div className="flex items-center justify-between mb-3">
              <h3 className="text-sm font-medium text-green-200">Soya Percentage</h3>
              <Activity className="h-6 w-6 text-green-400" />
            </div>
            <p className="text-3xl font-bold text-white mb-2">21%</p>
            <p className="text-xs text-green-300">of total feed volume</p>
            <div className="mt-3 w-full bg-gray-700 rounded-full h-2">
              <div className="bg-gradient-to-r from-green-400 to-emerald-400 h-2 rounded-full" style={{ width: '21%' }} />
            </div>
          </div>

          <div className="bg-gradient-to-br from-blue-900 to-cyan-900 rounded-xl shadow-xl p-6 border border-blue-700 hover:scale-105 transition-transform">
            <div className="flex items-center justify-between mb-3">
              <h3 className="text-sm font-medium text-blue-200">Active Producers</h3>
              <Users className="h-6 w-6 text-blue-400" />
            </div>
            <p className="text-3xl font-bold text-white mb-2">198</p>
            <p className="text-xs text-blue-300">across 7 feed companies</p>
            <div className="mt-3 flex gap-1">
              {[1,2,3,4,5].map(i => (
                <div key={i} className="flex-1 h-8 bg-blue-500 rounded opacity-${100 - (i * 15)}" />
              ))}
            </div>
          </div>
        </div>

        {/* Customer-Specific Soya Usage (if selected) */}
        {customerSoyaUsage && (
          <div className="bg-gradient-to-br from-purple-900/50 to-indigo-900/50 backdrop-blur-lg rounded-xl shadow-xl p-6 mb-6 border border-purple-600">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-xl font-semibold text-white flex items-center gap-2">
                <Users className="h-6 w-6 text-purple-400" />
                {customerSoyaUsage.name} - Proportionate Soya Usage
              </h3>
              <span className="text-sm text-purple-300 bg-purple-800/50 px-3 py-1 rounded-full">
                {customerSoyaUsage.percentage}% of total production
              </span>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <h4 className="text-sm font-medium text-gray-300 mb-3">Year 2 Attribution</h4>
                <div className="space-y-2">
                  <div className="flex justify-between items-center p-3 bg-gray-800/50 rounded-lg">
                    <span className="text-gray-300">Total Soya</span>
                    <span className="text-white font-bold">{customerSoyaUsage.year2.totalSoya} MT</span>
                  </div>
                  <div className="flex justify-between items-center p-2 bg-gray-800/30 rounded-lg">
                    <span className="text-gray-400 text-sm">Soy Bean</span>
                    <span className="text-yellow-400">{customerSoyaUsage.year2.soyBean} MT</span>
                  </div>
                  <div className="flex justify-between items-center p-2 bg-gray-800/30 rounded-lg">
                    <span className="text-gray-400 text-sm">Soy Hull</span>
                    <span className="text-orange-400">{customerSoyaUsage.year2.soyHull} MT</span>
                  </div>
                  <div className="flex justify-between items-center p-2 bg-gray-800/30 rounded-lg">
                    <span className="text-gray-400 text-sm">Soy Oil</span>
                    <span className="text-orange-300">{customerSoyaUsage.year2.soyOil} MT</span>
                  </div>
                </div>
              </div>
              <div>
                <h4 className="text-sm font-medium text-gray-300 mb-3">Year 3 Attribution</h4>
                <div className="space-y-2">
                  <div className="flex justify-between items-center p-3 bg-gray-800/50 rounded-lg">
                    <span className="text-gray-300">Total Soya</span>
                    <span className="text-white font-bold">{customerSoyaUsage.year3.totalSoya} MT</span>
                  </div>
                  <div className="flex justify-between items-center p-2 bg-gray-800/30 rounded-lg">
                    <span className="text-gray-400 text-sm">Soy Bean</span>
                    <span className="text-yellow-400">{customerSoyaUsage.year3.soyBean} MT</span>
                  </div>
                  <div className="flex justify-between items-center p-2 bg-gray-800/30 rounded-lg">
                    <span className="text-gray-400 text-sm">Soy Hull</span>
                    <span className="text-orange-400">{customerSoyaUsage.year3.soyHull} MT</span>
                  </div>
                  <div className="flex justify-between items-center p-2 bg-gray-800/30 rounded-lg">
                    <span className="text-gray-400 text-sm">Soy Oil</span>
                    <span className="text-orange-300">{customerSoyaUsage.year3.soyOil} MT</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Main Charts Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-6">
          {/* Soya Breakdown Treemap */}
          <div className="bg-gray-800/50 backdrop-blur-lg rounded-xl shadow-xl p-6 border border-gray-700">
            <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
              <Database className="h-5 w-5 text-yellow-400" />
              Soya Product Breakdown
            </h3>
            <ResponsiveContainer width="100%" height={350}>
              <Treemap
                data={soyaTreemapData}
                dataKey="size"
                aspectRatio={4 / 3}
                stroke="#1f2937"
                fill="#8884d8"
              >
                <Tooltip 
                  content={({ active, payload }) => {
                    if (active && payload && payload.length) {
                      const data = payload[0].payload;
                      return (
                        <div className="bg-gray-900 text-white p-3 rounded-lg shadow-xl border border-gray-700">
                          <p className="font-semibold">{data.name}</p>
                          <p className="text-sm">Tonnage: {data.size?.toLocaleString()} MT</p>
                          <p className="text-sm">Percentage: {data.percentage}%</p>
                        </div>
                      );
                    }
                    return null;
                  }}
                />
              </Treemap>
            </ResponsiveContainer>
            <div className="mt-4 grid grid-cols-3 gap-3">
              <div className="text-center">
                <div className="w-4 h-4 bg-yellow-500 rounded mx-auto mb-1" />
                <p className="text-xs text-gray-400">Soy Bean</p>
                <p className="text-sm font-semibold text-white">75%</p>
              </div>
              <div className="text-center">
                <div className="w-4 h-4 bg-orange-500 rounded mx-auto mb-1" />
                <p className="text-xs text-gray-400">Soy Hull</p>
                <p className="text-sm font-semibold text-white">20%</p>
              </div>
              <div className="text-center">
                <div className="w-4 h-4 bg-orange-300 rounded mx-auto mb-1" />
                <p className="text-xs text-gray-400">Soy Oil</p>
                <p className="text-sm font-semibold text-white">5%</p>
              </div>
            </div>
          </div>

          {/* Customer Volume Distribution */}
          <div className="bg-gray-800/50 backdrop-blur-lg rounded-xl shadow-xl p-6 border border-gray-700">
            <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
              <Building2 className="h-5 w-5 text-blue-400" />
              Customer Distribution & Impact
            </h3>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={customerData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                <XAxis dataKey="name" angle={-45} textAnchor="end" height={60} stroke="#9ca3af" />
                <YAxis stroke="#9ca3af" />
                <Tooltip content={<CustomTooltip />} />
                <Bar dataKey="percentage" fill={COLORS.primary} radius={[8, 8, 0, 0]}>
                  {customerData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={index === 0 ? COLORS.primary : index === 1 ? COLORS.secondary : COLORS.info} />
                  ))}
                </Bar>
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Feed Companies Section */}
        <div className="bg-gray-800/50 backdrop-blur-lg rounded-xl shadow-xl p-6 border border-gray-700 mb-6">
