            {expandedSections.feedCompanies ? <ChevronUp className="h-5 w-5 text-gray-400" /> : <ChevronDown className="h-5 w-5 text-gray-400" />}
          </div>

          {expandedSections.feedCompanies && (
            <div className="space-y-3">
              {feedCompanies.map((company, idx) => (
                <div key={idx} className="bg-gray-700/50 rounded-lg p-4 hover:bg-gray-700/70 transition-colors">
                  <div className="flex items-center justify-between mb-2">
                    <div className="flex items-center gap-3">
                      <Truck className="h-5 w-5 text-blue-400" />
                      <h4 className="font-semibold text-white">{company.name}</h4>
                    </div>
                    <div className="flex items-center gap-4">
                      <span className="text-sm text-gray-400">{company.tonnage.toLocaleString()} MT</span>
                      <span className="text-xs bg-blue-600/20 text-blue-400 px-2 py-1 rounded-full">{company.producers} producers</span>
                    </div>
                  </div>
                  <div className="ml-8">
                    <p className="text-xs text-gray-400 mb-1">Mills:</p>
                    <div className="flex flex-wrap gap-2">
                      {company.mills.map((mill, millIdx) => (
                        <span key={millIdx} className="text-xs bg-gray-600/50 text-gray-300 px-2 py-1 rounded">
                          {mill}
                        </span>
                      ))}
                    </div>
                  </div>
                  <div className="mt-2 ml-8">
                    <div className="w-full bg-gray-600 rounded-full h-2">
                      <div 
                        className="bg-gradient-to-r from-blue-400 to-blue-600 h-2 rounded-full transition-all"
                        style={{ width: `${(company.tonnage / 4500) * 100}%` }}
                      />
                    </div>
                  </div>
                </div>
              ))}

              <div className="mt-4 p-4 bg-blue-900/20 rounded-lg border border-blue-700/50">
                <div className="flex items-start gap-3">
                  <Info className="h-5 w-5 text-blue-400 mt-0.5" />
                  <div>
                    <p className="text-sm text-blue-200 font-medium">Supply Chain Summary</p>
                    <p className="text-xs text-blue-300 mt-1">
                      Total of {feedCompanies.length} feed companies operating {feedCompanies.reduce((sum, c) => sum + c.mills.length, 0)} mills, 
                      supplying {feedCompanies.reduce((sum, c) => sum + c.producers, 0)} producers
                    </p>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Data Quality Indicators */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div className="bg-gradient-to-br from-green-900/30 to-emerald-900/30 backdrop-blur-lg rounded-xl p-4 border border-green-700/50">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm text-green-300">Data Completeness</span>
              <span className="text-2xl font-bold text-green-400">94%</span>
            </div>
            <div className="w-full bg-gray-700 rounded-full h-2">
              <div className="bg-gradient-to-r from-green-400 to-emerald-400 h-2 rounded-full" style={{ width: '94%' }} />
            </div>
          </div>

          <div className="bg-gradient-to-br from-blue-900/30 to-cyan-900/30 backdrop-blur-lg rounded-xl p-4 border border-blue-700/50">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm text-blue-300">EUDR Compliance</span>
              <span className="text-2xl font-bold text-blue-400">87%</span>
            </div>
            <div className="w-full bg-gray-700 rounded-full h-2">
              <div className="bg-gradient-to-r from-blue-400 to-cyan-400 h-2 rounded-full" style={{ width: '87%' }} />
            </div>
          </div>

          <div className="bg-gradient-to-br from-purple-900/30 to-pink-900/30 backdrop-blur-lg rounded-xl p-4 border border-purple-700/50">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm text-purple-300">Traceability Score</span>
              <span className="text-2xl font-bold text-purple-400">91%</span>
            </div>
            <div className="w-full bg-gray-700 rounded-full h-2">
              <div className="bg-gradient-to-r from-purple-400 to-pink-400 h-2 rounded-full" style={{ width: '91%' }} />
            </div>
          </div>
        </div>

        {/* Footer */}
        <div className="mt-8 text-center">
          <p className="text-sm text-gray-500">
            Powered by Farmmetrics â€¢ Trouw Nutrition Database â€¢ Last sync: {new Date().toLocaleString()}
          </p>
          <p className="text-xs text-gray-600 mt-1">
            EUDR Compliance Module Integration Pending â€¢ AIC Partnership Active
          </p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;

